import express, { type Express, Request, Response, NextFunction } from "express";
import path from 'path';
import fs from 'fs';
import { fileURLToPath } from 'url';

// --- JAV√çT√ÅS: A HI√ÅNYZ√ì LOG F√úGGV√âNY VISSZAILLESZTVE ---
export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit", 
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export function serveStatic(app: Express) {
    // Megb√≠zhat√≥an meghat√°rozzuk a 'dist' mappa abszol√∫t √∫tvonal√°t.
    // Ez a m√≥dszer f√ºggetlen a szerver futtat√°si k√∂rnyezet√©t≈ël.
    const __filename = fileURLToPath(import.meta.url); // pl. /.../dist/server/static-server.js
    const __dirname = path.dirname(__filename);       // pl. /.../dist/server
    const distPath = path.resolve(__dirname, '..', '..', 'dist');   // Visszal√©p√ºnk a projekt gy√∂ker√©ig, majd a dist-be

    console.log(`[Static Server] Serving files from root directory: ${distPath}`);

    // Statikus f√°jlok (CSS, JS, k√©pek) kiszolg√°l√°sa a 'dist' mapp√°b√≥l.
    // Ez a middleware automatikusan megtal√°lja pl. a '/logo.png' vagy '/assets/index.css' f√°jlokat.
    app.use(express.static(distPath));

    // "Catch-all" √∫tvonal a kliensoldali routing (React Router) t√°mogat√°s√°hoz.
    // Ennek az API √∫tvonalak UT√ÅN kell lennie.
    app.get('*', (req: Request, res: Response, next: NextFunction) => {
        // Ha a k√©r√©s az API-hoz sz√≥l, azt nem itt kezelj√ºk, tov√°bbadjuk.
        if (req.path.startsWith('/api')) {
            return next();
        }

        // Minden m√°s esetben (pl. egy direkt link egy aloldalra) visszak√ºldj√ºk a f≈ë index.html f√°jlt,
        // hogy a React Router √°tvehesse az ir√°ny√≠t√°st.
        const indexPath = path.resolve(distPath, 'index.html');
        if (fs.existsSync(indexPath)) {
            res.sendFile(indexPath);
        } else {
            res.status(404).send('index.html not found in dist directory');
        }
    });
}

