import { memo, useState, useCallback, useRef, useEffect } from 'react';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';

interface StableRadioProps {
  value: string;
  onChange: (value: string) => void;
  options: Array<{ value: string; label: string; id: string }>;
  className?: string;
}

// Teljesen izol√°lt r√°di√≥gomb komponens f√ºggetlen state kezel√©ssel
const StableRadioComponent = ({ 
  value, 
  onChange, 
  options,
  className = "space-y-3"
}: StableRadioProps) => {

  // Helyi state hogy elker√ºlj√ºk a parent re-render triggereket
  const [internalValue, setInternalValue] = useState(value);
  const [isUpdating, setIsUpdating] = useState(false);
  const updateTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const lastReportedValueRef = useRef(value);

  // Szinkroniz√°ljuk a prop √©rt√©kkel, de csak ha nem √©ppen friss√≠t√ºnk
  useEffect(() => {
    if (!isUpdating && value !== internalValue) {
      setInternalValue(value);
      lastReportedValueRef.current = value;
    }
  }, [value, isUpdating, internalValue]);

  const handleSelection = useCallback((newValue: string) => {
    // Azonnal friss√≠tj√ºk a helyi state-et a UI responsiveness √©rdek√©ben
    setInternalValue(newValue);
    setIsUpdating(true);

    // T√∂r√∂lj√ºk a kor√°bbi timeout-ot
    if (updateTimeoutRef.current) {
      clearTimeout(updateTimeoutRef.current);
    }

    // Csak akkor jelents√ºk a parent-nek, ha t√©nyleg v√°ltozott az √©rt√©k
    updateTimeoutRef.current = setTimeout(() => {
      if (newValue !== lastReportedValueRef.current) {
        lastReportedValueRef.current = newValue;
        onChange(newValue);
      }
      setIsUpdating(false);
    }, 200); // R√∂vid de elegend≈ë k√©sleltet√©s
  }, [onChange]);

  // Cleanup
  useEffect(() => {
    return () => {
      if (updateTimeoutRef.current) {
        clearTimeout(updateTimeoutRef.current);
      }
    };
  }, []);

  // Teljes event elszigetel√©s
  const handleRadioClick = useCallback((e: React.MouseEvent, optionValue: string) => {
    e.stopPropagation();
    e.preventDefault();
    handleSelection(optionValue);
  }, [handleSelection]);

  const handleLabelClick = useCallback((e: React.MouseEvent, optionValue: string) => {
    e.stopPropagation();
    e.preventDefault();
    handleSelection(optionValue);
  }, [handleSelection]);

  const handleContainerClick = useCallback((e: React.MouseEvent) => {
    e.stopPropagation();
  }, []);

  return (
    <div 
      className={`stable-radio-container ${className}`} 
      onClick={handleContainerClick}
    >
      <RadioGroup
        value={internalValue}
        onValueChange={handleSelection}
        className="space-y-3"
      >
        {options.map((option) => (
          <div 
            key={option.value} 
            className="flex items-center space-x-3 stable-radio-item" 
            onClick={handleContainerClick}
          >
            <RadioGroupItem 
              value={option.value} 
              id={option.id}
              onClick={(e) => handleRadioClick(e, option.value)}
              className="stable-radio-input"
            />
            <Label 
              htmlFor={option.id} 
              className="cursor-pointer select-none stable-radio-label"
              onClick={(e) => handleLabelClick(e, option.value)}
            >
              {option.label}
            </Label>
          </div>
        ))}
      </RadioGroup>
    </div>
  );
};

export const StableRadio = memo(StableRadioComponent, (prevProps, nextProps) => {
  return (
    prevProps.value === nextProps.value &&
    prevProps.options === nextProps.options &&
    prevProps.className === nextProps.className
  );
});

