import { useState } from 'react';
import { ProtocolError } from '@shared/schema';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent } from '@/components/ui/card';
import { Download, Mail, Printer, FileText, Camera, Tag } from 'lucide-react';
import { useLanguageContext } from './language-provider';

interface ErrorExportProps {
  errors: ProtocolError[];
  protocolData?: {
    buildingAddress?: string;
    liftId?: string;
    inspectorName?: string;
    inspectionDate?: string;
  };
}

export function ErrorExport({ errors, protocolData }: ErrorExportProps) {
  const { t, language } = useLanguageContext();
  const [showPreview, setShowPreview] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);

  // Combine React state errors with localStorage errors
  const localStorageErrors = (() => {
    try {
      const stored = localStorage.getItem('protocol-errors');
      return stored ? JSON.parse(stored) : [];
    } catch {
      return [];
    }
  })();

  const allErrors = [...errors, ...localStorageErrors];

  const getSeverityColor = (severity: ProtocolError['severity']) => {
    switch (severity) {
      case 'critical': return 'destructive';
      case 'medium': return 'default';
      case 'low': return 'secondary';
      default: return 'outline';
    }
  };

  const getSeverityText = (severity: ProtocolError['severity']) => {
    switch (severity) {
      case 'critical':
        return language === 'hu' ? 'Kritikus' : 'Kritisch';
      case 'medium':
        return language === 'hu' ? 'K√∂zepes' : 'Mittel';
      case 'low':
        return language === 'hu' ? 'Alacsony' : 'Niedrig';
      default:
        return severity;
    }
  };

  const generatePDF = async () => {
    setIsGenerating(true);
    try {
      const response = await fetch('/api/errors/export-pdf', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          errors: allErrors,
          protocolData,
          language
        })
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `OTIS_Hibalista_${new Date().toISOString().slice(0, 10)}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      }
    } catch (error) {
      console.error('Error generating PDF:', error);
    } finally {
      setIsGenerating(false);
    }
  };

  const generateExcel = async () => {
    setIsGenerating(true);
    try {
      const response = await fetch('/api/errors/export-excel', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          errors: allErrors,
          protocolData,
          language
        })
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `OTIS_Hibalista_${new Date().toISOString().slice(0, 10)}.xlsx`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      }
    } catch (error) {
      console.error('Error generating Excel:', error);
    } finally {
      setIsGenerating(false);
    }
  };

  const sendEmail = async () => {
    // TODO: Implement email functionality with SendGrid
    alert(language === 'hu' ? 'Email funkci√≥ fejleszt√©s alatt' : 'Email-Funktion in Entwicklung');
  };

  const printReport = () => {
    window.print();
  };

  if (allErrors.length === 0) {
    return (
      <Card className="bg-green-50 border-green-200">
        <CardContent className="p-6 text-center">
          <div className="text-green-600 mb-2">‚úÖ</div>
          <h3 className="text-lg font-semibold text-green-800 mb-2">
            {language === 'hu' ? 'Nincs jelentett hiba' : 'Keine Fehler gemeldet'}
          </h3>
          <p className="text-green-600">
            {language === 'hu' 
              ? 'Az √°tv√©teli protokoll hibamentesen befejezve.'
              : 'Das Abnahmeprotokoll wurde fehlerfrei abgeschlossen.'}
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      <Card className="bg-white border border-gray-200 shadow-sm">
        <CardContent className="p-6">
          <div className="flex items-center justify-between mb-6">
            <div>
              <h2 className="text-xl font-semibold text-gray-800 mb-2">
                {language === 'hu' ? 'Hibalista Export√°l√°s' : 'Fehlerliste Export'}
              </h2>
              <p className="text-sm text-gray-600">
                {allErrors.length} {language === 'hu' ? 'hiba dokument√°lva' : 'Fehler dokumentiert'}
                {' ‚Ä¢ '}
                {allErrors.filter(e => e.images?.length > 0).length} {language === 'hu' ? 'fot√≥val' : 'mit Fotos'}
              </p>
            </div>
            <Button
              onClick={() => setShowPreview(true)}
              variant="outline"
              className="border-otis-blue text-otis-blue hover:bg-otis-blue hover:text-white"
            >
              <FileText className="h-4 w-4 mr-2" />
              {language === 'hu' ? 'El≈ën√©zet' : 'Vorschau'}
            </Button>
          </div>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <Button
              onClick={generatePDF}
              disabled={isGenerating}
              className="bg-red-600 hover:bg-red-700 text-white"
            >
              <FileText className="h-4 w-4 mr-2" />
              {isGenerating ? (language === 'hu' ? 'Gener√°l√°s...' : 'Erstellen...') : 'PDF'}
            </Button>

            <Button
              onClick={generateExcel}
              disabled={isGenerating}
              className="bg-green-600 hover:bg-green-700 text-white"
            >
              <Download className="h-4 w-4 mr-2" />
              {isGenerating ? (language === 'hu' ? 'Gener√°l√°s...' : 'Erstellen...') : 'Excel'}
            </Button>

            <Button
              onClick={sendEmail}
              className="bg-blue-600 hover:bg-blue-700 text-white"
            >
              <Mail className="h-4 w-4 mr-2" />
              {language === 'hu' ? 'Email' : 'E-Mail'}
            </Button>

            {/* Nyomtat√°s gomb - EGYS√âGES ST√çLUS */}
            <Button
              onClick={printReport}
              variant="outline"
              className="text-otis-blue border-2 border-otis-blue hover:bg-otis-blue hover:text-white active:bg-otis-blue active:text-white transition-colors"
            >
              <Printer className="h-4 w-4 mr-2" />
              {language === 'hu' ? 'Nyomtat√°s' : 'Drucken'}
            </Button>
          </div>

          {/* Quick Statistics */}
          <div className="mt-6 grid grid-cols-3 gap-4">
            <div className="text-center p-4 bg-red-50 rounded-lg">
              <div className="text-2xl font-bold text-red-600">
                {allErrors.filter(e => e.severity === 'critical').length}
              </div>
              <div className="text-sm text-red-600">{getSeverityText('critical')}</div>
            </div>
            <div className="text-center p-4 bg-yellow-50 rounded-lg">
              <div className="text-2xl font-bold text-yellow-600">
                {allErrors.filter(e => e.severity === 'medium').length}
              </div>
              <div className="text-sm text-yellow-600">{getSeverityText('medium')}</div>
            </div>
            <div className="text-center p-4 bg-blue-50 rounded-lg">
              <div className="text-2xl font-bold text-blue-600">
                {allErrors.filter(e => e.severity === 'low').length}
              </div>
              <div className="text-sm text-blue-600">{getSeverityText('low')}</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Preview Modal */}
      <Dialog open={showPreview} onOpenChange={setShowPreview}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>
              {language === 'hu' ? 'Hibalista El≈ën√©zet' : 'Fehlerliste Vorschau'}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-6" id="error-report-content">
            {/* Header */}
            <div className="text-center border-b pb-4">
              <h1 className="text-2xl font-bold text-otis-blue mb-2">
                OTIS {language === 'hu' ? 'Hibalista' : 'Fehlerliste'}
              </h1>
              <div className="text-sm text-gray-600 space-y-1">
                {protocolData?.buildingAddress && (
                  <p><strong>{language === 'hu' ? '√âp√ºlet:' : 'Geb√§ude:'}</strong> {protocolData.buildingAddress}</p>
                )}
                {protocolData?.liftId && (
                  <p><strong>{language === 'hu' ? 'Lift ID:' : 'Aufzug ID:'}</strong> {protocolData.liftId}</p>
                )}
                {protocolData?.inspectorName && (
                  <p><strong>{language === 'hu' ? 'Ellen≈ër:' : 'Pr√ºfer:'}</strong> {protocolData.inspectorName}</p>
                )}
                <p><strong>{language === 'hu' ? 'D√°tum:' : 'Datum:'}</strong> {new Date().toLocaleDateString()}</p>
              </div>
            </div>

            {/* Error List */}
            <div className="space-y-4">
              {allErrors.map((error, index) => (
                <Card key={error.id} className="border border-gray-200">
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between mb-3">
                      <div className="flex items-center space-x-3">
                        <span className="text-lg font-bold text-gray-500">#{index + 1}</span>
                        <Badge variant={getSeverityColor(error.severity)}>
                          {getSeverityText(error.severity)}
                        </Badge>
                      </div>
                      {error.images?.length > 0 && (
                        <div className="flex items-center text-sm text-gray-500">
                          <Camera className="h-4 w-4 mr-1" />
                          {error.images.length} {language === 'hu' ? 'fot√≥' : 'Foto(s)'}
                        </div>
                      )}
                    </div>

                    <h3 className="font-semibold text-gray-800 mb-2">{error.title}</h3>
                    <p className="text-gray-600 mb-3">{error.description}</p>

                    {error.images?.length > 0 && (
                      <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
                        {error.images.map((image: string, imgIndex: number) => (
                          <div key={imgIndex} className="relative">
                            <img
                              src={image}
                              alt={`${language === 'hu' ? 'Hiba fot√≥' : 'Fehlerfoto'} ${imgIndex + 1}`}
                              className="w-full h-32 object-cover rounded border"
                            />
                            <div className="absolute top-1 right-1 bg-black bg-opacity-50 text-white text-xs px-1 rounded">
                              {imgIndex + 1}
                            </div>
                          </div>
                        ))}
                      </div>
                    )}
                  </CardContent>
                </Card>
              ))}
            </div>

            {/* Footer */}
            <div className="text-center text-sm text-gray-500 border-t pt-4">
              <p>{language === 'hu' ? 'Gener√°lva' : 'Erstellt'}: {new Date().toLocaleString()}</p>
              <p>OTIS APROD - {language === 'hu' ? '√Åtv√©teli Protokoll Alkalmaz√°s' : 'Abnahmeprotokoll Anwendung'}</p>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

