// server/services/grounding-pdf-service.ts ‚Äì V√âGLEGES, DEPLOY-BIZTOS VERZI√ì (v10)

import { PDFDocument, PDFTextField, PDFButton } from 'pdf-lib';
import fontkit from '@pdf-lib/fontkit';
import fs from 'fs';
import path from 'path';
import { groundingPdfMapping } from '../config/grounding-pdf-mapping.js';
import { GroundingAnswer, FormData } from '../../shared/types.js';

export class GroundingPdfService {
  static async generateFilledPdf(formData: FormData): Promise<Buffer> {
    console.log('--- FUT A DEPLOY-BIZTOS, VASTAG BET≈∞S VERZI√ì! v10 ---');

    // 1Ô∏è‚É£ PDF √©s bet≈±t√≠pusok el≈ëk√©sz√≠t√©se (Helyes sorrend)
    const templatePath = path.resolve(process.cwd(), 'public/templates/Erdungskontrolle.pdf');
    const pdfBytes = fs.readFileSync(templatePath);
    const pdfDoc = await PDFDocument.load(pdfBytes);

    pdfDoc.registerFontkit(fontkit);

    const regularFontPath = path.resolve(process.cwd(), 'public/fonts/Roboto-Regular.ttf');
    const boldFontPath = path.resolve(process.cwd(), 'public/fonts/Roboto-Bold.ttf');
    const regularFontBytes = fs.readFileSync(regularFontPath);
    const boldFontBytes = fs.readFileSync(boldFontPath);
    const robotoFont = await pdfDoc.embedFont(regularFontBytes);
    const robotoBold = await pdfDoc.embedFont(boldFontBytes);

    const form = pdfDoc.getForm();

    // 2Ô∏è‚É£ Alapadatok √©s egy√©ni sz√∂vegek kit√∂lt√©se
    // √ñsszegy≈±jtj√ºk az √∂sszes kit√∂ltend≈ë sz√∂veges mez≈ët
    const allTextFields: { [key: string]: string | undefined } = { ...formData.customGroundingTexts };
    groundingPdfMapping.metadata.forEach(meta => {
        if (meta.appDataKey !== 'signature' && (formData as any)[meta.appDataKey]) {
            allTextFields[meta.pdfFieldName] = (formData as any)[meta.appDataKey];
        }
    });

    // Ciklus a sz√∂veges mez≈ëk kit√∂lt√©s√©re a sima bet≈±t√≠pussal
    for (const [pdfFieldName, textValue] of Object.entries(allTextFields)) {
        if (textValue && typeof textValue === 'string') {
            try {
                const field = form.getTextField(pdfFieldName);
                field.setText(textValue);
                field.updateAppearances(robotoFont); // Sima bet≈±t√≠pus a fejl√©chez √©s egy√©ni sz√∂vegekhez
            } catch (e) {
                console.warn(`‚ö†Ô∏è Sz√∂veges mez≈ë nem tal√°lhat√≥ vagy nem kompatibilis: "${pdfFieldName}"`);
            }
        }
    }

    // 3Ô∏è‚É£ Al√°√≠r√°s beilleszt√©se (k√ºl√∂n kezelve)
    const signatureValue = formData.signature;
    if (signatureValue && typeof signatureValue === 'string' && signatureValue.startsWith('data:image/png;base64,')) {
        try {
            const pngImage = await pdfDoc.embedPng(signatureValue);
            const imageField = form.getButton('signature');
            imageField.setImage(pngImage);
            console.log('‚úÖ Signature image inserted');
        } catch(e) {
            console.warn(`‚ö†Ô∏è Hiba az al√°√≠r√°sk√©p beilleszt√©sekor:`, e);
        }
    }

    // 4Ô∏è‚É£ F√∂ldel√©si k√©rd√©sek (OK / nicht OK / -) - NYELVF√úGG≈ê HIBAKI√çR√ÅSSAL
    const remarks: { punkt: string; bemerkung: string }[] = [];
    groundingPdfMapping.answers.forEach(({ questionId, okFieldName, notOkFieldName }) => {
      const answer = formData.groundingCheckAnswers?.[questionId];
      if (!answer) return;

      try {
        if (answer === 'ok' || answer === 'not_ok' || answer === 'not_applicable') {
            const isNotOk = answer === 'not_ok';
            const fieldName = isNotOk ? notOkFieldName : okFieldName;
            const textToSet = answer === 'not_applicable' ? '-' : 'X';

            const field = form.getTextField(fieldName);
            field.setText(textToSet);
            // ‚úÖ A GARANT√ÅLTAN M≈∞K√ñD≈ê MET√ìDUS: Csak a VASTAG bet≈±t√≠pust adjuk √°t
            field.updateAppearances(robotoBold); 

            if (isNotOk) {
                const punkt = okFieldName.replace('OK', '');

                // --- √öJ LOGIKA: NYELVF√úGG≈ê HIBALE√çR√ÅS ---
                // 1. Keress√ºk meg a konkr√©t hib√°t a formData.errors t√∂mbben az aktu√°lis questionId alapj√°n
                const specificError = formData.errors?.find(err => (err as any).context === questionId);

                // 2. A hiba le√≠r√°sa a 'bemerkung' mez≈ëbe. Ha nem tal√°lunk konkr√©t hib√°t, egy √°ltal√°nos sz√∂veg lesz a tartal√©k.
                let bemerkungText = `Hiba a ${punkt} pontn√°l.`; // Alap√©rtelmezett, tartal√©k sz√∂veg
                if (specificError && specificError.description) {
                    bemerkungText = specificError.description; // Fel√ºl√≠rjuk a konkr√©t, nyelvf√ºgg≈ë le√≠r√°ssal
                }

                // 3. A remarks t√∂mbh√∂z m√°r a dinamikus sz√∂veget adjuk hozz√°
                remarks.push({ punkt, bemerkung: bemerkungText });
            }
        }
      } catch (e) { 
        console.warn(`‚ö†Ô∏è Hiba a(z) ${questionId} v√°lasz be√≠r√°sakor.`, e);
      }
    });

    // 5Ô∏è‚É£ Bemerkung mez≈ëk kit√∂lt√©se
    if (remarks.length >= 1) {
        try {
            const row1 = groundingPdfMapping.remarks[0];
            const punktField1 = form.getTextField(row1.punktField);
            punktField1.setText(remarks[0].punkt);
            punktField1.updateAppearances(robotoBold); // Legyen a hiba is vastag

            const bemerkungField1 = form.getTextField(row1.bemerkungField);
            bemerkungField1.setText(remarks[0].bemerkung);
            bemerkungField1.updateAppearances(robotoBold);
        } catch (e) { console.warn(`‚ö†Ô∏è Hiba a Bemerkung 1. sor be√≠r√°sakor.`); }
    }
    if (remarks.length >= 2) {
        try {
            const row2 = groundingPdfMapping.remarks[1];
            const punktField2 = form.getTextField(row2.punktField);
            const bemerkungField2 = form.getTextField(row2.bemerkungField);

            if (remarks.length > 2) {
                bemerkungField2.setText('A tov√°bbi hib√°kat keresd a k√∂z√∂s hibalist√°ban');
                punktField2.setText('');
            } else {
                punktField2.setText(remarks[1].punkt);
                bemerkungField2.setText(remarks[1].bemerkung);
            }
            punktField2.updateAppearances(robotoBold);
            bemerkungField2.updateAppearances(robotoBold);
        } catch (e) { console.warn(`‚ö†Ô∏è Hiba a Bemerkung 2. sor be√≠r√°sakor.`); }
    }

    // ====================================================================
    // ‚úÖ √öJ BLOKK: EREDM√âNY MEZ≈êK AUTOMATIKUS KIT√ñLT√âSE (EGYEDI LOGIK√ÅVAL)
    // ====================================================================
    try {
        console.log('üìä Eredm√©ny mez≈ëk ki√©rt√©kel√©se...');

        // Seg√©df√ºggv√©ny: van-e hiba egy adott csoportban
        const hasErrorInGroup = (groupPrefix: string) => {
            const questionsInGroup = groundingPdfMapping.answers.filter(
                q => q.questionId.startsWith(groupPrefix)
            );
            return questionsInGroup.some(
                question => formData.groundingCheckAnswers?.[question.questionId] === 'not_ok'
            );
        };

        // X1: Hiba van az OK1/ (Maschinenraum) csoportban
        const hasErrorInX1 = hasErrorInGroup('OK1/');
        if (hasErrorInX1) {
            console.log(`‚ùóÔ∏è Hiba √©szlelve az OK1/ csoportban. X1 mez≈ë bejel√∂l√©se.`);
            const field = form.getTextField('X1');
            field.setText('X');
            field.updateAppearances(robotoBold);
        }

        // X2: Hiba van az OK2/ - OK5/ B√ÅRMELYIK csoportban
        const hasErrorInX2 = hasErrorInGroup('OK2/') || 
                             hasErrorInGroup('OK3/') || 
                             hasErrorInGroup('OK4/') || 
                             hasErrorInGroup('OK5/');
        if (hasErrorInX2) {
            console.log(`‚ùóÔ∏è Hiba √©szlelve az OK2/-OK5/ csoportok valamelyik√©ben. X2 mez≈ë bejel√∂l√©se.`);
            const field = form.getTextField('X2');
            field.setText('X');
            field.updateAppearances(robotoBold);
        }

        // X3: Hiba van az OK1/ √âS (OK2/ - OK5/ valamelyik√©ben)
        const hasErrorInX3 = hasErrorInX1 && hasErrorInX2;
        if (hasErrorInX3) {
            console.log(`‚ùóÔ∏è Hiba √©szlelve az OK1/ √âS OK2/-OK5/ csoportokban. X3 mez≈ë bejel√∂l√©se.`);
            const field = form.getTextField('X3');
            field.setText('X');
            field.updateAppearances(robotoBold);
        }

        // X4: Hiba van B√ÅRMELYIK csoportban (OK1/ - OK5/)
        const hasErrorInX4 = hasErrorInGroup('OK1/') || 
                             hasErrorInGroup('OK2/') || 
                             hasErrorInGroup('OK3/') || 
                             hasErrorInGroup('OK4/') || 
                             hasErrorInGroup('OK5/');
        if (hasErrorInX4) {
            console.log(`‚ùóÔ∏è Hiba √©szlelve valamelyik csoportban. X4 mez≈ë bejel√∂l√©se.`);
            const field = form.getTextField('X4');
            field.setText('X');
            field.updateAppearances(robotoBold);
        }

        // X5: Nincs automatikus logika - k√©zi kit√∂lt√©sre hagyva
        console.log('‚ÑπÔ∏è X5 mez≈ë k√©zi kit√∂lt√©sre hagyva (ut√≥ellen≈ërz√©s).');

    } catch (e) {
        console.warn(`‚ö†Ô∏è Hiba t√∂rt√©nt az eredm√©ny mez≈ëk automatikus kit√∂lt√©se sor√°n.`, e);
    }

    // 6Ô∏è‚É£ V√©gleges√≠t√©s √©s ment√©s
    form.flatten();
    const filledPdfBytes = await pdfDoc.save();
    return Buffer.from(filledPdfBytes);
  }
}

src/pages/questionnaire.tsx.content:
import { useState, useEffect, useMemo, useCallback } from 'react';
import { create, all } from 'mathjs';
import { Question, AnswerValue, ProtocolError } from '@shared/schema';
import { Button } from '@/components/ui/button';
import { SmartHelpWizard } from '@/components/smart-help-wizard';
import PageHeader from '@/components/PageHeader';
import { IsolatedQuestion } from '@/components/isolated-question';
import { TrueFalseGroup } from '@/components/true-false-group';
import { ErrorList } from '@/components/error-list';
import { QuestionGroupHeader } from '@/components/question-group-header';
import { useLanguageContext } from '@/components/language-provider';
import { ArrowLeft, ArrowRight, Save, Check, X } from 'lucide-react';
import { MeasurementBlock } from '@/components/measurement-block';
import { useConditionalQuestionFilter, updateAnswersWithDisabled } from '@/components/conditional-question-filter';

interface QuestionnaireProps {
  receptionDate: string;
  onReceptionDateChange: (date: string) => void;
  answers: Record<string, AnswerValue>;
  onAnswerChange: (questionId: string, value: AnswerValue) => void;
  errors: ProtocolError[];
  onErrorsChange: (errors: ProtocolError[]) => void;
  onNext: () => void;
  onSave: () => void;
  language: 'hu' | 'de';
  onAdminAccess?: () => void;
  onHome?: () => void;
  onStartNew?: () => void;
  onPageChange?: (page: number) => void;
  onQuestionChange?: (questionId: string) => void;
  currentPage: number;
  currentQuestionId: string;
}

const math = create(all);

// Fallback questions if no template is loaded
const FALLBACK_QUESTIONS: Partial<Question>[] = [
  {
    id: 'q1',
    questionId: 'q1',
    title: 'Receiver name / √Åtvev≈ë neve',
    type: 'text' as const,
    required: true,
  },
  {
    id: 'q2',
    questionId: 'q2',
    title: 'Elevator installation complete? / Lift telep√≠t√©s k√©sz?',
    type: 'checkbox' as const,
    required: true,
  },
] as Question[];

// === JAV√çT√ÅS 1: ELT√ÅVOL√çTOTTUK A memo() WRAPPERT ===
function Questionnaire({
  receptionDate,
  onReceptionDateChange,
  answers,
  onAnswerChange,
  errors,
  onErrorsChange,
  onNext,
  onSave,
  language,
  onAdminAccess,
  onHome,
  onStartNew,
  onPageChange,
  onQuestionChange,
  currentPage: pageFromApp,
  currentQuestionId,
}: QuestionnaireProps) {
  const { t } = useLanguageContext();

  const [allQuestions, setAllQuestions] = useState<Question[]>([]);
  const [questionsLoading, setQuestionsLoading] = useState(true);
  const [saveStatus, setSaveStatus] = useState<'idle' | 'saving' | 'saved' | 'error'>('idle');

  // Local answers for immediate UI response
  const [localAnswers, setLocalAnswers] = useState<Record<string, AnswerValue>>(answers);

  // Sync with parent answers
  useEffect(() => {
    setLocalAnswers(answers);
  }, [answers]);

  // Apply conditional filtering
  const { filteredQuestions, filteredCount } = useConditionalQuestionFilter(allQuestions, localAnswers);

  // Auto-set disabled questions to "n.a." AND clear "n.a." from re-enabled questions
  useEffect(() => {
    if (allQuestions.length > 0 && filteredQuestions.length > 0) {
      const updatedAnswers = updateAnswersWithDisabled(localAnswers, allQuestions, filteredQuestions);

      // ADDITIONAL LOGIC: Clear "n.a." from questions that are now visible again
      filteredQuestions.forEach(q => {
        // If a question is visible and its value is "n.a.", clear it
        if (updatedAnswers[q.id] === 'n.a.') {
          updatedAnswers[q.id] = '';
        }
      });

      const hasChanges = Object.keys(updatedAnswers).some(key => 
        updatedAnswers[key] !== localAnswers[key]
      );

      if (hasChanges) {
        console.log('üéØ Auto-setting disabled questions to "n.a." and clearing re-enabled questions', {
          totalQuestions: allQuestions.length,
          visibleQuestions: filteredQuestions.length,
          disabledCount: allQuestions.length - filteredQuestions.length
        });

        setLocalAnswers(updatedAnswers);

        // Update parent
        Object.entries(updatedAnswers).forEach(([questionKey, value]) => {
          if (value !== localAnswers[questionKey]) {
            onAnswerChange(questionKey, value);
          }
        });
      }
    }
  }, [filteredQuestions, allQuestions, localAnswers, onAnswerChange]);

  // Load questions ONCE on mount
  useEffect(() => {
    const loadQuestions = async () => {
      try {
        setQuestionsLoading(true);
        const response = await fetch(`/api/questions/${language}`);

        if (response.ok) {
          const questionsData = await response.json();
          console.log('‚úÖ Questions loaded:', questionsData.length);
          setAllQuestions(questionsData);
        } else {
          console.warn('‚ö†Ô∏è No active template found, using empty question list');
          setAllQuestions([]);
        }
      } catch (error) {
        console.error('‚ùå Error loading questions:', error);
        setAllQuestions([]);
      } finally {
        setQuestionsLoading(false);
      }
    };

    loadQuestions();
  }, [language]);

  // Group questions and paginate
  const { questionGroups, totalPages, currentQuestions, currentGroup } = useMemo(() => {
    const groups = filteredQuestions.reduce((acc: Record<string, Question[]>, question: Question) => {
      const groupName = question.groupName;

      if (!groupName) {
        return acc;
      }

      if (!acc[groupName]) {
        acc[groupName] = [];
      }
      acc[groupName].push(question);
      return acc;
    }, {} as Record<string, Question[]>);

    // Sort questions by groupOrder
    Object.keys(groups).forEach(groupName => {
      groups[groupName].sort((a: Question, b: Question) => (a.groupOrder || 0) - (b.groupOrder || 0));
    });

    // Convert to array
    const groupsArray = Object.entries(groups)
      .filter(([groupName, questions]) => questions.length > 0)
      .map(([groupName, questions]) => ({
        name: groupName,
        questions,
        questionCount: questions.length
      }));

    const total = groupsArray.length;
    const currentGroupData = groupsArray[pageFromApp] || { name: '', questions: [], questionCount: 0 };

    return { 
      questionGroups: groupsArray, 
      totalPages: total, 
      currentQuestions: currentGroupData.questions,
      currentGroup: currentGroupData
    };
  }, [filteredQuestions, pageFromApp, filteredCount]);

  // Calculate derived values for calculated questions
  useEffect(() => {
    const calculatedQuestions = allQuestions.filter(q => q.type === 'calculated');

    calculatedQuestions.forEach(q => {
      if (q.calculationFormula && q.calculationInputs) {
        try {
          const scope: Record<string, any> = {};

          Object.entries(localAnswers).forEach(([key, value]) => {
            if (typeof value === 'string' && !isNaN(Number(value))) {
              scope[key] = Number(value);
            } else {
              scope[key] = value;
            }
          });

          const result = math.evaluate(q.calculationFormula, scope);

          if (typeof result === 'number' && !isNaN(result)) {
            const roundedResult = Math.round(result * 100) / 100;
            if (localAnswers[q.id] !== roundedResult) {
              setLocalAnswers(prev => ({ ...prev, [q.id]: roundedResult }));
              onAnswerChange(q.id, roundedResult);
            }
          }
        } catch (error) {
          if (localAnswers[q.id] !== undefined) {
            setLocalAnswers(prev => {
              const newAnswers = { ...prev };
              delete newAnswers[q.id];
              return newAnswers;
            });
            onAnswerChange(q.id, undefined);
          }
        }
      }
    });
  }, [allQuestions, localAnswers, onAnswerChange]);

  // Error handlers
  const handleAddError = useCallback((error: Omit<ProtocolError, 'id'>) => {
    const newError: ProtocolError = {
      ...error,
      id: Date.now().toString(),
    };
    onErrorsChange([...errors, newError]);
  }, [onErrorsChange, errors]);

  const handleEditError = useCallback((id: string, updatedError: Omit<ProtocolError, 'id'>) => {
    onErrorsChange(
      errors.map((error: ProtocolError) =>
        error.id === id ? { ...updatedError, id } : error
      )
    );
  }, [onErrorsChange, errors]);

  const handleDeleteError = useCallback((id: string) => {
    onErrorsChange(errors.filter((error: ProtocolError) => error.id !== id));
  }, [onErrorsChange, errors]);

  // Local answer change handler
  const handleLocalAnswerChange = useCallback((questionId: string, value: AnswerValue) => {
    setLocalAnswers(prev => ({
      ...prev,
      [questionId]: value
    }));
    onAnswerChange(questionId, value);
    onQuestionChange?.(questionId);
  }, [onAnswerChange, onQuestionChange]);

  // Check if can proceed
  const canProceed = useMemo(() => {
    const requiredQuestionsOnPage = (currentQuestions as Question[]).filter(q => q.required);
    if (requiredQuestionsOnPage.length === 0) return true;

    return requiredQuestionsOnPage.every(q => {
      const answer = localAnswers[q.id];
      return answer !== undefined && answer !== null && answer !== '';
    });
  }, [currentQuestions, localAnswers]);

  const isLastPage = pageFromApp === totalPages - 1;
  const progressPercent = totalPages > 0 ? Math.round(((pageFromApp + 1) / totalPages) * 100) : 0;

  // Save handler
  const handleSave = useCallback(() => {
    setSaveStatus('saving');
    try {
      onSave?.();
      setSaveStatus('saved');
      setTimeout(() => setSaveStatus('idle'), 3000);
    } catch (error) {
      console.error('Save failed:', error);
      setSaveStatus('error');
      setTimeout(() => setSaveStatus('idle'), 3000);
    }
  }, [onSave]);

  // Navigation handlers
  const handleNextPage = useCallback(() => {
    onPageChange?.(pageFromApp + 1);
  }, [pageFromApp, onPageChange]);

  const handlePreviousPage = useCallback(() => {
    onPageChange?.(Math.max(0, pageFromApp - 1));
  }, [pageFromApp, onPageChange]);

  return (
    <div className="min-h-screen bg-light-surface relative">
      {/* === JAV√çT√ÅS 2: ELT√ÅVOL√çTOTTUK A FIX TITLE PROPOT === */}
      <PageHeader
        onHome={onHome}
        onStartNew={onStartNew}
        onAdminAccess={onAdminAccess}
        receptionDate={receptionDate}
        onReceptionDateChange={onReceptionDateChange}
        totalSteps={totalPages + 1}
        currentStep={pageFromApp}
        stepType="questionnaire"
        currentPage={pageFromApp + 1}
        currentQuestionId={currentQuestionId}
        errors={errors}
      />

      <main className="max-w-7xl mx-auto px-6 py-8">
        {questionGroups.length > 0 && currentGroup && (
          <QuestionGroupHeader
            groupName={currentGroup.name}
            questionCount={currentGroup.questionCount}
            totalGroups={totalPages}
            currentGroupIndex={pageFromApp}
            language={language}
          />
        )}

        <div className="mb-8">
          {pageFromApp === 0 || pageFromApp === 1 ? (
            <div className="grid grid-cols-2 gap-8">
              {(currentQuestions as Question[]).map((question: Question) => (
                <IsolatedQuestion
                  key={question.id}
                  question={question}
                  value={localAnswers[question.id] ?? ""}
                  onChange={(value) => handleLocalAnswerChange(question.id, value)}
                />
              ))}
            </div>
          ) : (() => {
            // FIXED: Smart rendering based on question type composition
            const radioQuestions = (currentQuestions as Question[]).filter(q => q.type === 'radio' || q.type === 'checkbox' || q.type === 'yes_no_na');
            const measurementQuestions = (currentQuestions as Question[]).filter(q => q.type === 'measurement' || q.type === 'calculated');
            const otherQuestions = (currentQuestions as Question[]).filter(q => 
              q.type !== 'radio' && 
              q.type !== 'checkbox' && 
              q.type !== 'yes_no_na' && 
              q.type !== 'measurement' && 
              q.type !== 'calculated'
            );

            const hasOnlyRadio = radioQuestions.length === currentQuestions.length;
            const hasOnlyMeasurement = measurementQuestions.length === currentQuestions.length;
            const isMixed = (radioQuestions.length > 0 || measurementQuestions.length > 0) && otherQuestions.length > 0;

            // If ONLY radio/checkbox questions with standard true/false options ‚Üí use TrueFalseGroup
            // (TrueFalseGroup hardcodes true/false/n.a. options, so only use for pure boolean groups)
            const allRadioAreBoolean = radioQuestions.every(q => 
              !q.options || q.options.length === 0 || 
              (Array.isArray(q.options) && q.options.every((opt: string) => 
                ['true', 'false', 'n.a.', 'yes', 'no', 'igen', 'nem'].includes(opt.toLowerCase())
              ))
            );

            if (hasOnlyRadio && allRadioAreBoolean) {
              return (
                <TrueFalseGroup
                  questions={radioQuestions}
                  values={localAnswers}
                  onChange={handleLocalAnswerChange}
                  groupName={currentGroup?.name || 'K√©rd√©sek'}
                />
              );
            }

            // If ONLY measurement/calculated questions ‚Üí use MeasurementBlock
            if (hasOnlyMeasurement) {
              return (
                <MeasurementBlock
                  questions={measurementQuestions}
                  values={localAnswers}
                  onChange={handleLocalAnswerChange}
                  onAddError={handleAddError}
                />
              );
            }

            // If MIXED types OR radio with custom options ‚Üí render each question individually
            // This ensures each question type renders with its correct UI component
            return (
              <div className="space-y-6">
                {measurementQuestions.length > 0 && (
                  <MeasurementBlock
                    questions={measurementQuestions}
                    values={localAnswers}
                    onChange={handleLocalAnswerChange}
                    onAddError={handleAddError}
                  />
                )}
                {(radioQuestions.length > 0 || otherQuestions.length > 0) && (
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                    {[...radioQuestions, ...otherQuestions].map((question: Question) => (
                      <IsolatedQuestion
                        key={question.id}
                        question={question}
                        value={localAnswers[question.id] ?? ""}
                        onChange={(value) => handleLocalAnswerChange(question.id, value)}
                      />
                    ))}
                  </div>
                )}
              </div>
            );
          })()}
        </div>

        <div className="mb-8">
          <ErrorList
            errors={errors}
            onAddError={handleAddError}
            onEditError={handleEditError}
            onDeleteError={handleDeleteError}
          />
        </div>

        <div className="flex justify-between items-center">
  {/* Bal oldali gomb (Vissza) */}
  <Button
  variant="outline"
  onClick={handlePreviousPage}
  disabled={pageFromApp === 0}
  className="flex items-center border-otis-blue text-otis-blue hover:bg-otis-blue hover:text-white"
>
  <ArrowLeft className="h-4 w-4 mr-2" />
  {t.previous}
</Button>

  {/* ===== EZ AZ √öJ, BEILLESZTETT R√âSZ ===== */}
  {/* K√∂z√©ps≈ë gomb (AI) */}
  <SmartHelpWizard
    currentPage={pageFromApp + 1}
    formData={localAnswers}
    currentQuestionId={currentQuestionId}
    errors={errors}
  />
  {/* ======================================= */}

  {/* Jobb oldali gombok (Ment√©s, Tov√°bb) */}
  <div className="flex space-x-4">
    <Button
  variant="outline"
  onClick={handleSave}
  disabled={saveStatus === 'saving'}
  className={`flex items-center space-x-2 ${
    saveStatus === 'saved' ? 'bg-green-100 border-green-300 text-green-700' :
    saveStatus === 'error' ? 'bg-red-100 border-red-300 text-red-700' :
    'border-otis-blue text-otis-blue hover:bg-otis-blue hover:text-white' // EZ AZ √öJ ALAP√âRTELMEZETT ST√çLUS
  }`}
>
              {saveStatus === 'saving' ? (
    <>
      <div className="animate-spin h-4 w-4 mr-2 border-2 border-gray-300 border-t-blue-600 rounded-full"></div>
      {t.saving}
    </>
  ) : saveStatus === 'saved' ? (
    <>
      <Check className="h-4 w-4 mr-2 text-green-600" />
      {t.saved}
    </>
  ) : saveStatus === 'error' ? (
    <>
      <X className="h-4 w-4 mr-2 text-red-600" />
      {t.error}
    </>
  ) : (
    <>
      <Save className="h-4 w-4 mr-2" />
      {t.save}
    </>
  )}
</Button>

            {isLastPage ? (
              <Button
    type="button"
    onClick={onNext}
    disabled={!canProceed}
    className={`flex items-center border border-otis-blue disabled:bg-gray-400 disabled:cursor-not-allowed ${
      canProceed 
        ? 'bg-otis-blue text-white hover:bg-white hover:text-otis-blue cursor-pointer' 
        : 'bg-gray-400 text-white cursor-not-allowed'
    }`}
  >
                {t.next}
    <ArrowRight className="h-4 w-4 ml-2" />
  </Button>
) : (
  <Button
    type="button"
    onClick={handleNextPage}
    disabled={!canProceed}
    className={`flex items-center border border-otis-blue disabled:bg-gray-400 disabled:cursor-not-allowed ${
      canProceed 
        ? 'bg-otis-blue text-white hover:bg-white hover:text-otis-blue cursor-pointer' 
        : 'bg-gray-400 text-white cursor-not-allowed'
    }`}
  >
    {t.next} {canProceed ? '‚úì' : '‚úó'}
    <ArrowRight className="h-4 w-4 ml-2" />
  </Button>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default Questionnaire;

