// server/app.ts - JAVASOLT, V√âGLEGES VERZI√ì

import express from 'express';
import 'dotenv/config'; // Fontos, hogy ez legyen az egyik els≈ë import
import { registerRoutes } from './routes.js';

export async function createApp() {
  const app = express();
  app.use(express.json());

  // Loggol√≥ middleware a bej√∂v≈ë k√©r√©sekhez
  app.use((req, res, next) => {
    if (req.path.startsWith("/api")) {
      console.log(`[API Request] ${req.method} ${req.path}`);
    }
    next();
  });

  // Route-ok regisztr√°l√°sa, megv√°rjuk, am√≠g befejez≈ëdik
  await registerRoutes(app);

  // === JAVASOLT KIEG√âSZ√çT√âS ===
  // Glob√°lis hibakezel≈ë middleware, ami elkap minden nem lekezelt hib√°t a route-okb√≥l.
  // Ezt mindig a route-ok regisztr√°l√°sa UT√ÅN kell elhelyezni.
  app.use((err: any, req: express.Request, res: express.Response, next: express.NextFunction) => {
    console.error(`[Global Error Handler] Unhandled error on path: ${req.path}`, err);
    const status = err.status || 500;
    const message = err.message || 'Internal Server Error';
    res.status(status).json({ message });
  });
  // ============================

  return app;
}

