// src/components/true-false-radio.tsx - TELJES, √öJ VERZI√ì

import { memo } from 'react';
import { Label } from '@/components/ui/label';
import { StyledRadioGroup } from './StyledRadioGroup'; // Import√°ljuk az √∫j komponenst

interface TrueFalseRadioProps {
  questionId: string;
  questionTitle: string;
  value: string;
  onChange: (value: string) => void;
  // √öj prop, hogy tudjuk, milyen gombokat kell megjelen√≠teni
  questionType: 'radio' | 'checkbox' | string; 
}

// Opci√≥k defini√°l√°sa a k√ºl√∂nb√∂z≈ë t√≠pusokhoz
const trueFalseOptions = [
  { value: 'true', label: 'Igen' },
  { value: 'false', label: 'Nem' },
];

const yesNoNaOptions = [
  { value: 'yes', label: 'Igen' },
  { value: 'no', label: 'Nem' },
  { value: 'na', label: 'N.A.' },
];

export const TrueFalseRadio = memo(({ questionId, questionTitle, value, onChange, questionType }: TrueFalseRadioProps) => {
  // A `parser` a `true_false` k√©rd√©seket `radio`-v√°, a `yes_no_na` k√©rd√©seket `checkbox`-sz√° alak√≠tja.
  // Ezt a logik√°t haszn√°ljuk a megfelel≈ë gombok kiv√°laszt√°s√°hoz.
  const options = questionType === 'radio' ? trueFalseOptions : yesNoNaOptions;

  return (
    <div className="grid grid-cols-[1fr_auto] gap-4 items-center py-3 border-b border-gray-100 last:border-b-0">
      {/* K√©rd√©s C√≠me */}
      <Label className="text-sm font-medium text-gray-800 pr-4">
        {questionTitle}
      </Label>

      {/* √öj, st√≠lusos r√°di√≥gomb csoport */}
      <div className="flex justify-center">
        <StyledRadioGroup
          questionId={questionId}
          value={value}
          onChange={onChange}
          options={options}
          direction="vertical"
        />
      </div>
    </div>
  );
});

TrueFalseRadio.displayName = 'TrueFalseRadio';

