import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuSeparator,
} from '@/components/ui/dropdown-menu';

interface InfinityInputProps {
  value: string | number;
  onChange: (value: string) => void;
}

export function InfinityInput({ value, onChange }: InfinityInputProps) {
  // Ez a bels≈ë √°llapot kezeli, hogy mi jelenjen meg a sz√°m beviteli mez≈ëben
  const isNumeric = value !== '‚àû' && value !== '-';
  const [inputValue, setInputValue] = useState(isNumeric ? value.toString() : '');

  // Biztos√≠tja, hogy a bels≈ë √°llapot friss√ºlj√∂n, ha a k√ºls≈ë √©rt√©k v√°ltozik
  useEffect(() => {
    const isNumericProp = value !== '‚àû' && value !== '-';
    setInputValue(isNumericProp ? value.toString() : '');
  }, [value]);

  const handleNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const numericValue = e.target.value.replace(/[^0-9.]/g, '');
    setInputValue(numericValue);
    onChange(numericValue);
  };

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
  variant="outline"
  className={`w-full font-normal ${
    (value === '‚àû' || value === '-')
      ? 'text-xl font-bold justify-center' // <-- √öJ ST√çLUSOK, HA ‚àû VAGY -
      : 'justify-start'                    // <-- Eredeti st√≠lus egy√©bk√©nt
  }`}
>
  {value || <span className="text-muted-foreground">-</span>}
</Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent
        className="w-40"
        onCloseAutoFocus={(e) => e.preventDefault()}
      >
        {/* "-" (Nem relev√°ns) opci√≥ */}
        <DropdownMenuItem onSelect={() => onChange('-')}>
          <span className="text-lg font-bold mx-auto">-</span>
        </DropdownMenuItem>

        <DropdownMenuSeparator />

        {/* V√©gtelen jel kiv√°laszt√°sa */}
        <DropdownMenuItem onSelect={() => onChange('‚àû')}>
          <span className="text-lg font-bold mx-auto">‚àû</span>
        </DropdownMenuItem>

        <DropdownMenuSeparator />

        {/* Numerikus √©rt√©k bevitele */}
        <div className="p-2">
          <Input
            type="text"
            placeholder="√ârt√©k (MŒ©)"
            className="text-center"
            value={inputValue}
            onChange={handleNumberChange}
            onClick={(e) => e.stopPropagation()}
            autoFocus
          />
        </div>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

