// src/components/StyledRadioGroup.tsx

import { memo } from 'react';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';

interface Option {
  value: string;
  label: string;
}

interface StyledRadioGroupProps {
  questionId: string;
  value: string;
  onChange: (value: string) => void;
  options: Option[];
}

const getColorClasses = (value: string, currentValue: string) => {
  switch (value) {
    case 'yes':
    case 'true':
      return currentValue === value
        ? 'bg-green-600 text-white border-green-600 shadow-md'
        : 'bg-white text-gray-800 border-gray-300 hover:bg-green-50';
    case 'no':
    case 'false':
      return currentValue === value
        ? 'bg-red-600 text-white border-red-600 shadow-md'
        : 'bg-white text-gray-800 border-gray-300 hover:bg-red-50';
    case 'na':
      return currentValue === value
        ? 'bg-gray-600 text-white border-gray-600 shadow-md'
        : 'bg-white text-gray-800 border-gray-300 hover:bg-gray-100';
    default:
      return 'bg-white text-gray-800 border-gray-300';
  }
};

export const StyledRadioGroup = memo(({ questionId, value, onChange, options }: StyledRadioGroupProps) => {
  return (
    <RadioGroup
      value={value}
      onValueChange={onChange}
      className="flex items-center gap-x-2 sm:gap-x-3"
    >
      {options.map((option) => (
        <div key={option.value}>
          <RadioGroupItem value={option.value} id={`${questionId}_${option.value}`} className="sr-only" />
          <Label
            htmlFor={`${questionId}_${option.value}`}
            className={`
              inline-flex items-center justify-center rounded-full px-3 py-1 sm:px-4 sm:py-1.5 text-sm font-semibold border cursor-pointer transition-all
              ${getColorClasses(option.value, value)}
            `}
          >
            {option.label}
          </Label>
        </div>
      ))}
    </RadioGroup>
  );
});

StyledRadioGroup.displayName = 'StyledRadioGroup';

scripts/build-vercel.js.content:
#!/usr/bin/env node

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

console.log('üèóÔ∏è  OTIS APROD v0.4.8 - Vercel Build Script');
console.log('===========================================');

// Ensure we're in the right directory
if (!fs.existsSync('vercel.json')) {
    console.error('‚ùå Error: vercel.json not found');
    process.exit(1);
}

try {
    // Build client for production
    console.log('üì¶ Building client application...');
    process.chdir('client');
    execSync('npm run build', { stdio: 'inherit' });
    process.chdir('..');

    // Verify build output
    const distPath = path.join('client', 'dist');
    if (!fs.existsSync(distPath)) {
        throw new Error('Client build failed - dist directory not found');
    }

    // Copy necessary files to dist
    const filesToCopy = [
        'public/manifest.json',
        'public/sw.js', 
        'public/offline.html',
        'public/pwa-64x64.png',
        'public/pwa-192x192.png',
        'public/pwa-512x512.png'
    ];

    console.log('üìã Copying PWA files to dist...');
    filesToCopy.forEach(file => {
        const src = path.join(file);
        const dest = path.join('client', 'dist', path.basename(file));

        if (fs.existsSync(src)) {
            fs.copyFileSync(src, dest);
            console.log(`‚úÖ Copied ${file}`);
        } else {
            console.warn(`‚ö†Ô∏è  Warning: ${file} not found`);
        }
    });

    // Create package.json for Vercel if it doesn't exist
    const clientPackageJson = path.join('client', 'package.json');
    if (!fs.existsSync(clientPackageJson)) {
        console.log('üìù Creating client package.json...');
        const packageContent = {
            name: 'otis-aprod-client',
            version: '0.4.8',
            scripts: {
                build: 'vite build'
            }
        };
        fs.writeFileSync(clientPackageJson, JSON.stringify(packageContent, null, 2));
    }

    console.log('‚úÖ Build completed successfully!');
    console.log('üöÄ Ready for Vercel deployment');

} catch (error) {
    console.error('‚ùå Build failed:', error.message);
    process.exit(1);
}

