import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { useLanguageContext } from '@/components/language-provider';
import { HelpCircle, Lightbulb, AlertTriangle, CheckCircle, Brain, Zap } from 'lucide-react';

interface HelpSuggestion {
  id: string;
  type: 'tip' | 'warning' | 'info' | 'success';
  title: string;
  description: string;
  context: string;
  priority: 'high' | 'medium' | 'low';
}

interface SmartHelpWizardProps {
  currentPage: number;
  formData: any;
  currentQuestionId?: string;
  errors: any[];
}

export function SmartHelpWizard({ currentPage, formData, currentQuestionId, errors }: SmartHelpWizardProps) {
  const { t, language } = useLanguageContext();
  const [suggestions, setSuggestions] = useState<HelpSuggestion[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [aiThinking, setAiThinking] = useState(false);

  // Smart AI Analysis Engine - Helyi intelligencia
  const analyzeContext = async () => {
    setAiThinking(true);
    const newSuggestions: HelpSuggestion[] = [];

    // 1. Oldal-specifikus kontextu√°lis seg√≠ts√©g
    if (currentPage === 1) {
      newSuggestions.push({
        id: 'general_info',
        type: 'info',
        title: language === 'hu' ? '√Åltal√°nos adatok kit√∂lt√©se' : 'Allgemeine Daten ausf√ºllen',
        description: language === 'hu' 
          ? 'Gy≈ëz≈ëdj√∂n meg r√≥la, hogy az √∂sszes k√∂telez≈ë mez≈ë ki van t√∂ltve. A pontos adatok kritikusak a protokoll √©rv√©nyess√©g√©hez.'
          : 'Stellen Sie sicher, dass alle Pflichtfelder ausgef√ºllt sind. Genaue Daten sind kritisch f√ºr die Protokollg√ºltigkeit.',
        context: 'page_1_general',
        priority: 'medium'
      });
    }

    if (currentPage === 4) {
      // M√©r√©si adatok intelligens elemz√©se
      const measurementData = formData.measurements || {};
      const filledMeasurements = Object.values(measurementData).filter(v => v).length;

      if (filledMeasurements > 0) {
        newSuggestions.push({
          id: 'measurement_progress',
          type: 'success',
          title: language === 'hu' ? 'M√©r√©si adatok folyamatban' : 'Messdaten in Bearbeitung',
          description: language === 'hu' 
            ? `${filledMeasurements} m√©r√©si √©rt√©k m√°r r√∂gz√≠tve. Ellen≈ërizze az √©rt√©kek pontoss√°g√°t.`
            : `${filledMeasurements} Messwerte bereits erfasst. √úberpr√ºfen Sie die Genauigkeit der Werte.`,
          context: 'measurements_active',
          priority: 'medium'
        });
      }

      // M√©r√©si √©rt√©kek valid√°ci√≥
      Object.entries(measurementData).forEach(([key, value]) => {
        if (value && typeof value === 'string') {
          const numValue = parseFloat(value);
          if (isNaN(numValue)) {
            newSuggestions.push({
              id: `invalid_measurement_${key}`,
              type: 'warning',
              title: language === 'hu' ? '√ârv√©nytelen m√©r√©si √©rt√©k' : 'Ung√ºltiger Messwert',
              description: language === 'hu' 
                ? `A ${key} mez≈ë √©rv√©nytelen sz√°mot tartalmaz. K√©rj√ºk, ellen≈ërizze a bevitt √©rt√©ket.`
                : `Das Feld ${key} enth√§lt eine ung√ºltige Zahl. Bitte √ºberpr√ºfen Sie den eingegebenen Wert.`,
              context: `measurement_${key}`,
              priority: 'high'
            });
          }
        }
      });
    }

    if (currentPage === 5) {
      // Niedervolt m√©r√©sek elemz√©se
      newSuggestions.push({
        id: 'niedervolt_info',
        type: 'info',
        title: language === 'hu' ? 'Niedervolt m√©r√©si jegyz≈ëk√∂nyv' : 'Niedervolt Messprotokoll',
        description: language === 'hu' 
          ? 'A Niedervolt Installations Verordnung art.14 szerinti m√©r√©sek. Minden m√©r√©si t√≠pusn√°l 3 √©rt√©k r√∂gz√≠thet≈ë.'
          : 'Messungen nach Niedervolt Installations Verordnung art.14. F√ºr jeden Messtyp k√∂nnen 3 Werte erfasst werden.',
        context: 'niedervolt_page',
        priority: 'medium'
      });
    }

    // 2. Hib√°k alapj√°n intelligens javaslatok
    if (errors && errors.length > 0) {
      newSuggestions.push({
        id: 'error_detected',
        type: 'warning',
        title: language === 'hu' ? 'Hib√°k √©szlelve' : 'Fehler erkannt',
        description: language === 'hu' 
          ? `${errors.length} hiba ker√ºlt r√∂gz√≠t√©sre. Ellen≈ërizze a protokoll pontoss√°g√°t.`
          : `${errors.length} Fehler wurden erfasst. √úberpr√ºfen Sie die Protokollgenauigkeit.`,
        context: 'errors_present',
        priority: 'high'
      });
    }

    // 3. Okos kieg√©sz√≠t√©si javaslatok
    const formFields = Object.keys(formData);
    const emptyFields = formFields.filter(key => !formData[key] || formData[key] === '');

    if (emptyFields.length > 0 && currentPage < 5) {
      newSuggestions.push({
        id: 'completion_suggestion',
        type: 'tip',
        title: language === 'hu' ? 'Kit√∂lt√©s optimaliz√°l√°sa' : 'Vervollst√§ndigung optimieren',
        description: language === 'hu' 
          ? `${emptyFields.length} mez≈ë m√©g kit√∂lt√©sre v√°r. A teljes protokoll pontosabb eredm√©nyeket ad.`
          : `${emptyFields.length} Felder warten noch auf Ausf√ºllung. Ein vollst√§ndiges Protokoll liefert genauere Ergebnisse.`,
        context: 'completion_optimization',
        priority: 'low'
      });
    }

    // 4. Aktu√°lis k√©rd√©s kontextu√°lis seg√≠ts√©g
    if (currentQuestionId) {
      const contextualHelp = getQuestionContextualHelp(currentQuestionId);
      if (contextualHelp) {
        newSuggestions.push(contextualHelp);
      }
    }

    // 5. Teljes√≠tm√©nyre vonatkoz√≥ javaslatok
    if (currentPage === 3) {
      newSuggestions.push({
        id: 'modernization_tip',
        type: 'tip',
        title: language === 'hu' ? 'Moderniz√°ci√≥s tippek' : 'Modernisierungstipps',
        description: language === 'hu' 
          ? 'A moderniz√°ci√≥s k√©rd√©sek alapj√°n OTIS aj√°nl√°sokat adhat a lift optimaliz√°l√°s√°hoz.'
          : 'Basierend auf den Modernisierungsfragen kann OTIS Empfehlungen zur Aufzugoptimierung geben.',
        context: 'modernization_page',
        priority: 'low'
      });
    }

    setTimeout(() => {
      setSuggestions(newSuggestions);
      setAiThinking(false);
    }, 800); // R√∂vid AI "gondolkod√°si" id≈ë a realisztikus √©rz√©s√©rt
  };

  // K√©rd√©s-specifikus kontextu√°lis seg√≠ts√©g
  const getQuestionContextualHelp = (questionId: string): HelpSuggestion | null => {
    const helpMap: Record<string, HelpSuggestion> = {
      '1': {
        id: 'question_1_help',
        type: 'info',
        title: language === 'hu' ? '√Åtvev≈ë neve' : 'Name des √úbernehmers',
        description: language === 'hu' 
          ? 'Adja meg a protokollt √°tvev≈ë szem√©ly teljes nev√©t. Ez √°ltal√°ban a l√©tes√≠tm√©ny felel≈ës szem√©lye.'
          : 'Geben Sie den vollst√§ndigen Namen der Person ein, die das Protokoll √ºbernimmt. Dies ist normalerweise die verantwortliche Person der Anlage.',
        context: 'question_1',
        priority: 'medium'
      },
      'm1': {
        id: 'measurement_m1_help',
        type: 'tip',
        title: language === 'hu' ? 'Isolationsmessung' : 'Isolationsmessung',
        description: language === 'hu' 
          ? 'Az izol√°ci√≥s ellen√°ll√°s m√©r√©se. Tipikus √©rt√©kek: >1 MŒ©. Alacsonyabb √©rt√©kek hib√°ra utalhatnak.'
          : 'Messung des Isolationswiderstands. Typische Werte: >1 MŒ©. Niedrigere Werte k√∂nnen auf Fehler hinweisen.',
        context: 'measurement_m1',
        priority: 'high'
      }
    };

    return helpMap[questionId] || null;
  };

  useEffect(() => {
    analyzeContext();
  }, [currentPage, formData, currentQuestionId, errors]);

  const getSuggestionIcon = (type: string) => {
    switch (type) {
      case 'tip': return <Lightbulb className="h-4 w-4 text-yellow-600" />;
      case 'warning': return <AlertTriangle className="h-4 w-4 text-orange-600" />;
      case 'success': return <CheckCircle className="h-4 w-4 text-green-600" />;
      default: return <HelpCircle className="h-4 w-4 text-blue-600" />;
    }
  };

  const getSuggestionColor = (type: string) => {
    switch (type) {
      case 'tip': return 'border-yellow-200 bg-yellow-50';
      case 'warning': return 'border-orange-200 bg-orange-50';
      case 'success': return 'border-green-200 bg-green-50';
      default: return 'border-blue-200 bg-blue-50';
    }
  };

  const prioritySuggestions = suggestions.sort((a, b) => {
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    return priorityOrder[b.priority] - priorityOrder[a.priority];
  });

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button
          variant="outline"
          size="sm"
          className="bg-gradient-to-r from-blue-500 to-purple-600 text-white border-0 shadow-lg hover:shadow-xl transition-all duration-300"
        >
          <Brain className="h-4 w-4 mr-2" />
          {language === 'hu' ? 'AI Seg√≠t≈ë' : 'AI Hilfe'}
          {suggestions.length > 0 && (
            <Badge variant="secondary" className="ml-2 bg-white text-blue-600">
              {suggestions.length}
            </Badge>
          )}
