import { useState, useEffect, useMemo, useCallback, useRef } from 'react';
import { Question, AnswerValue, ProtocolError } from '@shared/schema';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Progress } from '@/components/ui/progress';
import { Label } from '@/components/ui/label';
import { IsolatedQuestion } from '@/components/isolated-question';
import { ErrorList } from '@/components/error-list';
import { ErrorExport } from '@/components/error-export';
import { useLanguageContext } from '@/components/language-provider';
import { ArrowLeft, ArrowRight, Save, Settings, Home } from 'lucide-react';

interface StableQuestionnaireProps {
  receptionDate: string;
  onReceptionDateChange: (date: string) => void;
  answers: Record<string, AnswerValue>;
  onAnswerChange: (questionId: string, value: AnswerValue) => void;
  errors: ProtocolError[];
  onErrorsChange: (errors: ProtocolError[]) => void;
  onNext: () => void;
  onSave: () => void;
  language: 'hu' | 'de';
  onAdminAccess?: () => void;
  onHome?: () => void;
}

export function StableQuestionnaire({
  receptionDate,
  onReceptionDateChange,
  answers,
  onAnswerChange,
  errors,
  onErrorsChange,
  onNext,
  onSave,
  language,
  onAdminAccess,
  onHome,
}: StableQuestionnaireProps) {
  const { t } = useLanguageContext();
  const [questions, setQuestions] = useState<Question[]>([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(true);

  // Use ref to store answers locally to prevent remounts
  const localAnswersRef = useRef<Record<string, AnswerValue>>(answers);

  // Sync ref with props
  useEffect(() => {
    localAnswersRef.current = answers;
  }, [answers]);

  // Load questions only once
  useEffect(() => {
    const loadQuestions = async () => {
      try {
        const response = await fetch(`/api/questions/${language}`);
        if (response.ok) {
          const data = await response.json();
          console.log("Questions loaded:", data.length);
          setQuestions(data);
        }
      } catch (error) {
        console.error('Failed to load questions:', error);
      } finally {
        setLoading(false);
      }
    };

    loadQuestions();
  }, []); // No dependencies - load once only

  // Stable answer handler that doesn't cause re-mounts
  const handleAnswerChangeStable = useCallback((questionId: string, value: AnswerValue) => {
    // Update local ref immediately
    localAnswersRef.current = {
      ...localAnswersRef.current,
      [questionId]: value
    };

    // Debounce parent update to prevent remounts
    setTimeout(() => {
      onAnswerChange(questionId, value);
    }, 100);
  }, [onAnswerChange]);

  const handleAddError = useCallback((error: Omit<ProtocolError, 'id'>) => {
    const newError: ProtocolError = {
      ...error,
      id: Date.now().toString(),
    };
    onErrorsChange([...errors, newError]);
  }, [onErrorsChange, errors]);

  const handleEditError = useCallback((id: string, updatedError: Omit<ProtocolError, 'id'>) => {
    onErrorsChange(
      errors.map((error: ProtocolError) =>
        error.id === id ? { ...updatedError, id } : error
      )
    );
  }, [onErrorsChange, errors]);

  const handleDeleteError = useCallback((id: string) => {
    onErrorsChange(errors.filter((error: ProtocolError) => error.id !== id));
  }, [onErrorsChange, errors]);

  const questionsPerPage = 4;
  const totalPages = Math.ceil(questions.length / questionsPerPage);
  const currentQuestions = questions.slice(
    currentPage * questionsPerPage,
    (currentPage + 1) * questionsPerPage
  );

  const canProceed = () => {
    const requiredQuestions = currentQuestions.filter(q => q.required);
    return requiredQuestions.every(q => localAnswersRef.current[q.id] !== undefined);
  };

  const isLastPage = currentPage === totalPages - 1;

  if (loading) {
    return (
      <div className="min-h-screen bg-light-surface flex items-center justify-center">
        <div className="text-lg">{language === 'de' ? 'Fragen werden geladen...' : 'K√©rd√©sek bet√∂lt√©se...'}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-light-surface">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            {/* Logo, Home and Title */}
            <div className="flex items-center">
              <img 
                src="/attached_assets/otis-elevators-seeklogo_1753525178175.png" 
                alt="OTIS Logo" 
                className="h-8 w-8 mr-4"
              />
              {onHome && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={onHome}
                  className="text-gray-600 hover:text-gray-800 mr-4"
                  title={language === 'de' ? 'Startseite' : 'Kezd≈ëlap'}
                >
                  <Home className="h-4 w-4" />
                </Button>
              )}
              <span className="text-lg font-medium text-gray-800">{t.title}</span>
            </div>

            {/* Date Picker and Admin */}
            <div className="flex items-center space-x-4">
              <Label className="text-sm font-medium text-gray-600">{t.receptionDate}</Label>
              <Input
                type="date"
                value={receptionDate}
                onChange={(e) => onReceptionDateChange(e.target.value)}
                className="w-auto"
              />
              {onAdminAccess && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={onAdminAccess}
                  className="text-gray-600 hover:text-gray-800"
                  title="Admin"
                >
                  <Settings className="h-4 w-4" />
                </Button>
              )}
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-6 py-8">
        {/* Progress Bar */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-2">
            <span className="text-sm font-medium text-gray-600">
              {t.progress}: {currentPage + 1} / {totalPages}
            </span>
            <span className="text-sm text-gray-500">
              {Math.round(((currentPage + 1) / totalPages) * 100)}%
            </span>
          </div>
          <Progress 
            value={((currentPage + 1) / totalPages) * 100} 
            className="h-2"
          />
        </div>

        {/* Questions */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
          <div className="space-y-6">
            {currentQuestions.map((question) => (
              <div key={question.id} className="space-y-3">
                <Label className="text-sm font-medium text-gray-700">
                  {question.title}
                  {question.required && <span className="text-red-500 ml-1">*</span>}
                </Label>

                {question.type === 'text' && (
                  <Input
                    type="text"
                    value={localAnswersRef.current[question.id] as string || ''}
                    onChange={(e) => handleAnswerChangeStable(question.id, e.target.value)}
                    className="w-full"
                    placeholder={question.placeholder || ''}
                  />
                )}

                {question.type === 'yes_no_na' && (
                  <div className="space-y-2">
                    {['yes', 'no', 'na'].map((option) => (
                      <label key={option} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="radio"
                          name={question.id}
                          value={option}
                          checked={localAnswersRef.current[question.id] === option}
                          onChange={(e) => {
                            if (e.target.checked) {
                              handleAnswerChangeStable(question.id, option);
                            }
                          }}
                          className="w-4 h-4"
                        />
                        <span className="text-sm text-gray-700">
                          {option === 'yes' ? t.yes : option === 'no' ? t.no : t.notApplicable}
                        </span>
                      </label>
                    ))}
                  </div>
                )}

                {question.type === 'number' && (
                  <Input
                    type="number"
                    value={localAnswersRef.current[question.id] as number || ''}
                    onChange={(e) => handleAnswerChangeStable(question.id, parseInt(e.target.value) || 0)}
                    className="w-full"
                    placeholder={question.placeholder || ''}
                  />
                )}

                {question.placeholder && (
                  <p className="text-xs text-gray-500">{question.placeholder}</p>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Error Management */}
        <ErrorList
          errors={errors}
          onAddError={handleAddError}
          onEditError={handleEditError}
          onDeleteError={handleDeleteError}
        />

        {/* Error Export - only show if there are errors */}
        {(errors.length > 0 || JSON.parse(localStorage.getItem('protocol-errors') || '[]').length > 0) && (
          <ErrorExport 
            errors={errors}
            protocolData={{
              buildingAddress: localAnswersRef.current['building_address'] as string,
              liftId: localAnswersRef.current['lift_id'] as string,
              inspectorName: localAnswersRef.current['inspector_name'] as string,
              inspectionDate: receptionDate
            }}
          />
        )}

        {/* Navigation */}
        <div className="flex justify-between items-center mt-8">
          <Button
            variant="outline"
            onClick={() => setCurrentPage(Math.max(0, currentPage - 1))}
            disabled={currentPage === 0}
            className="flex items-center space-x-2"
          >
            <ArrowLeft className="h-4 w-4" />
            <span>{t.previous}</span>
          </Button>

          <div className="flex space-x-4">
            <Button
              variant="outline"
              onClick={onSave}
              className="flex items-center space-x-2"
            >
              <Save className="h-4 w-4" />
              <span>{t.save}</span>
            </Button>

            {isLastPage ? (
              <Button
                onClick={onNext}
                disabled={!canProceed()}
                className="bg-otis-blue hover:bg-otis-blue/90"
              >
                {t.complete}
              </Button>
            ) : (
              <Button
                onClick={() => setCurrentPage(currentPage + 1)}
                disabled={!canProceed()}
                className="flex items-center space-x-2 bg-otis-blue hover:bg-otis-blue/90"
              >
                <span>{t.next}</span>
                <ArrowRight className="h-4 w-4" />
              </Button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

server/services/xml-excel-service.ts.content:
import JSZip from 'jszip';
import { XMLParser, XMLBuilder } from 'fast-xml-parser';
import * as fs from 'fs';
import { storage } from '../storage.js';
import type { FormData } from '../../shared/types.js';
import type { XmlExcelPayload, ParsedRow } from "../../shared/types.js";

// FormData interface imported from types

class XmlExcelService {
  private xmlParser = new XMLParser({
    ignoreAttributes: false,
    attributeNamePrefix: '@_',
    textNodeName: '#text',
    parseAttributeValue: true,
    parseTagValue: true,
    trimValues: true
  });

  private xmlBuilder = new XMLBuilder({
    ignoreAttributes: false,
    attributeNamePrefix: '@_',
    textNodeName: '#text',
    suppressEmptyNode: true,
    processEntities: false
  });

  async generateExcelFromTemplate(formData: FormData, language: string): Promise<Buffer> {
    try {
      // Get the active protocol template
      const protocolTemplate = await storage.getActiveTemplate('protocol', language);

      if (!protocolTemplate) {
        throw new Error('No active protocol template found');
      }

      console.log(`Using protocol template: ${protocolTemplate.name} (${protocolTemplate.fileName})`);

      // Read template file
      const templateBuffer = fs.readFileSync(protocolTemplate.filePath);

      // Get question configs for cell mapping
      const questionsTemplate = await storage.getActiveTemplate('questions', language);
      let questionConfigs: any[] = [];

      if (questionsTemplate) {
        questionConfigs = await storage.getQuestionConfigsByTemplate(questionsTemplate.id);
        console.log('Loaded question configs:', questionConfigs.length);
      }

      // Process the Excel template with XML manipulation
      return await this.populateTemplateWithXml(templateBuffer, formData, questionConfigs, language);

    } catch (error) {
      console.error('Error generating Excel from template:', error);
      throw error;
    }
  }

  private async populateTemplateWithXml(
    templateBuffer: Buffer, 
    formData: FormData, 
    questionConfigs: any[], 
    language: string
  ): Promise<Buffer> {
    try {
      // Load the Excel file as a ZIP archive
      const zip = await JSZip.loadAsync(templateBuffer);

      // Find worksheet files (usually xl/worksheets/sheet1.xml, sheet2.xml, etc.)
      const worksheetFiles = Object.keys(zip.files).filter(name => 
        name.startsWith('xl/worksheets/') && name.endsWith('.xml')
      );

      if (worksheetFiles.length === 0) {
        throw new Error('No worksheet files found in Excel template');
      }

      // Process the first worksheet (assuming single sheet template)
      const sheetFile = worksheetFiles[0];
      console.log(`Processing worksheet: ${sheetFile}`);

      // Read the worksheet XML
      const worksheetXml = await zip.file(sheetFile)!.async('text');

      // Parse XML to object
      const worksheetObj = this.xmlParser.parse(worksheetXml);

      // Create cell mappings based on question configs
      const cellMappings = this.createCellMappings(formData, questionConfigs, language);

      // Update cell values in the worksheet object
      this.updateCellValues(worksheetObj, cellMappings);

      // Convert back to XML
      const updatedXml = this.xmlBuilder.build(worksheetObj);

      // Update the ZIP with modified worksheet
      zip.file(sheetFile, updatedXml);

      // Generate the final Excel buffer
      const result = await zip.generateAsync({ 
        type: 'nodebuffer',
        compression: 'DEFLATE',
        compressionOptions: { level: 6 }
      });

      console.log(`Successfully filled ${cellMappings.length} cells using XML manipulation`);
      return result;

    } catch (error) {
      console.error('Error in XML manipulation:', error);
      throw error;
    }
  }

  private createCellMappings(formData: FormData, questionConfigs: any[], language: string): any[] {
    const mappings: any[] = [];

    // Add answers based on question configs
    Object.entries(formData.answers).forEach(([questionId, answer]) => {
      const config = questionConfigs.find(q => q.questionId === questionId);

      if (config && config.cellReference && answer !== '' && answer !== null && answer !== undefined) {

        // Handle yes_no_na questions specially - put X in appropriate column
        if (config.type === 'yes_no_na') {
          console.log(`Processing yes_no_na question ${questionId}: ${answer}, cellRef: ${config.cellReference}`);

          // Parse comma-separated cell references (A68,B68,C68)
          const cellRefs = config.cellReference.split(',').map((cell: string) => cell.trim());

          if (cellRefs.length !== 3) {
            console.error(`Yes_no_na question ${questionId} must have exactly 3 cell references (A,B,C), got: ${cellRefs}`);
            // Fallback to old logic if format is wrong
            mappings.push({
              cell: config.cellReference,
              value: this.formatAnswer(answer, language),
              label: config.title || `Question ${questionId}`
            });
            return;
          }

          const [yesCell, noCell, naCell] = cellRefs;
          console.log(`Cell mapping: YES=${yesCell}, NO=${noCell}, NA=${naCell}`);

          // Add X to the appropriate column based on answer
          if (answer === 'yes') {
            console.log(`Adding X to YES cell: ${yesCell}`);
            mappings.push({
              cell: yesCell,
              value: 'X',
              label: `${config.title} - Igen`
            });
          } else if (answer === 'no') {
            console.log(`Adding X to NO cell: ${noCell}`);
            mappings.push({
              cell: noCell,
              value: 'X', 
              label: `${config.title} - Nem`
            });
          } else if (answer === 'na') {
            console.log(`Adding X to NA cell: ${naCell}`);
            mappings.push({
              cell: naCell,
              value: 'X',
              label: `${config.title} - Nem alkalmazhat√≥`
            });
          }
        } else {
          // Handle other question types normally
          mappings.push({
            cell: config.cellReference,
            value: this.formatAnswer(answer, language),
            label: config.title || `Question ${questionId}`
          });
        }
      }
    });

    // Add signature name if available
    if (formData.signatureName && !mappings.find(m => m.cell === 'F9')) {
      mappings.push({
        cell: 'F9',
        value: formData.signatureName,
        label: 'Signature name'
      });
    }

    return mappings;
  }

  private updateCellValues(worksheetObj: any, cellMappings: any[]) {
    try {
      // Navigate to the worksheet data structure
      const worksheet = worksheetObj.worksheet;
      if (!worksheet) {
        throw new Error('Invalid worksheet structure - no worksheet found');
      }

      // Initialize sheetData if it doesn't exist
      if (!worksheet.sheetData) {
        worksheet.sheetData = { row: [] };
      }

      // Initialize rows array
      if (!worksheet.sheetData.row) {
        worksheet.sheetData.row = [];
      }

      const rows = Array.isArray(worksheet.sheetData.row) ? worksheet.sheetData.row : [worksheet.sheetData.row];

      cellMappings.forEach(mapping => {
        const { cell, value } = mapping;
        const { row: rowNum } = this.parseCellReference(cell);

        // Find or create the row
        let targetRow = rows.find((r: any) => r && r['@_r'] === rowNum);
        if (!targetRow) {
          targetRow = { '@_r': rowNum, c: [] };
          rows.push(targetRow);
        }

        // Ensure row.c is an array
        if (!targetRow.c) {
          targetRow.c = [];
        } else if (!Array.isArray(targetRow.c)) {
          targetRow.c = [targetRow.c];
        }

        // Find or create the cell
        let targetCell = targetRow.c.find((c: any) => c && c['@_r'] === cell);
        if (!targetCell) {
          targetCell = { '@_r': cell };
          targetRow.c.push(targetCell);
        }

        // Set cell value using inline string format for maximum compatibility
        targetCell['@_t'] = 'inlineStr';
        targetCell.is = { t: value };

        console.log(`XML: Set ${cell} = "${value}"`);
      });

      // Update the worksheet with modified rows
      worksheet.sheetData.row = rows;

    } catch (error) {
      console.error('Error updating cell values:', error);
      throw error;
    }
  }

  private parseCellReference(cellRef: string): { row: number, col: string } {
    const match = cellRef.match(/^([A-Z]+)(\d+)$/);
    if (!match) {
      throw new Error(`Invalid cell reference: ${cellRef}`);
    }

    return {
      row: parseInt(match[2], 10),
      col: match[1]
    };
  }



  private formatAnswer(value: any, language: string): string {
    if (typeof value === 'boolean') {
      return language === 'hu' ? (value ? 'Igen' : 'Nem') : (value ? 'Yes' : 'No');
    }

    if (value === 'yes') {
      return language === 'hu' ? 'Igen' : 'Yes';
    }

    if (value === 'no') {
      return language === 'hu' ? 'Nem' : 'No';
    }

    if (value === 'na') {
      return language === 'hu' ? 'N/A' : 'N/A';
    }

    return String(value);
  }
}

export const xmlExcelService = new XmlExcelService();

