import { memo, useCallback } from 'react';

// JAV√çTVA: A props interface most m√°r value-t √©s onChange-et v√°r
interface CacheRadioProps {
  questionId: string;
  value: string | undefined; // Az aktu√°lis kiv√°lasztott √©rt√©k a sz√ºl≈ët≈ël
  options: Array<{ value: string; label: string }>;
  onChange: (value: string) => void; // F√ºggv√©ny, amivel a sz√ºl≈ënek jelz√ºnk
}

// JAV√çTVA: Nincs t√∂bb√© glob√°lis cache √©s bels≈ë √°llapot
export const CacheRadio = memo(({ questionId, value, options, onChange }: CacheRadioProps) => {

  // JAV√çTVA: A handleChange most m√°r csak a sz√ºl≈ët h√≠vja meg.
  // A useCallback haszn√°lata itt is j√≥ gyakorlat.
  const handleChange = useCallback((newValue: string) => {
    console.log(`Radio changed: ${questionId} = ${newValue}`);
    onChange(newValue);
  }, [onChange]);

  return (
    <div className="space-y-3">
      {options.map((option) => {
        const inputId = `${questionId}-${option.value}`;
        return (
          <div key={option.value} className="flex items-center space-x-3">
            <input
              type="radio"
              id={inputId}
              name={questionId}
              value={option.value}
              // JAV√çTVA: A `checked` √°llapotot a sz√ºl≈ët≈ël kapott `value` prop hat√°rozza meg
              checked={value === option.value}
              onChange={(e) => {
                // Nem kell stopPropagation, √©s a handleChange m√°r useCallback-be van csomagolva
                if (e.target.checked) {
                  handleChange(option.value);
                }
              }}
              className="w-5 h-5 text-otis-blue bg-gray-100 border-gray-300 focus:ring-otis-blue focus:ring-2 focus:outline-none"
            />
            <label 
              htmlFor={inputId}
              className="text-sm font-medium text-gray-700 cursor-pointer select-none"
            >
              {option.label}
            </label>
          </div>
        );
      })}
    </div>
  );
});

CacheRadio.displayName = 'CacheRadio';

// A glob√°lis cache-re √©p√ºl≈ë f√ºggv√©nyek feleslegess√© v√°ltak, t√∂r√∂lhet≈ëk.
// Vagy a kor√°bbi mint√°hoz hasonl√≥an ki√ºr√≠theted ≈ëket egy figyelmeztet√©ssel.
export const getAllCachedValues = (): Record<string, string> => {
  console.warn('‚ö†Ô∏è getAllCachedValues() is deprecated. Use React state instead.');
  return {};
};

