      result: 'Eredm√©ny'
    }
  },
  german: {
    sectionTitle: 'Messdaten und Berechnungen',
    headers: {
      measurement: 'Messung',
      value: 'Wert', 
      unit: 'Einheit',
      limits: 'Grenzwerte',
      result: 'Ergebnis'
    }
  }
};

// Helper function to validate measurement values
export function validateMeasurement(value: number, minValue?: number, maxValue?: number): { isValid: boolean; error?: string } {
  if (isNaN(value)) {
    return { isValid: false, error: 'Invalid numeric value' };
  }

  if (minValue !== undefined && value < minValue) {
    return { isValid: false, error: `Value ${value} is below minimum ${minValue}` };
  }

  if (maxValue !== undefined && value > maxValue) {
    return { isValid: false, error: `Value ${value} is above maximum ${maxValue}` };
  }

  return { isValid: true };
}

// Helper function to evaluate calculation formulas safely
export function evaluateFormula(formula: string, values: Record<string, number>): { result: number | null; error?: string } {
  try {
    // Replace variable names with values
    let processedFormula = formula;
    for (const [key, value] of Object.entries(values)) {
      if (isNaN(value)) {
        return { result: null, error: `Missing or invalid value for ${key}` };
      }
      processedFormula = processedFormula.replace(new RegExp(`\\b${key}\\b`, 'g'), value.toString());
    }

    // Basic security check - only allow numbers, operators, parentheses
    if (!/^[0-9+\-*/.() ]+$/.test(processedFormula)) {
      return { result: null, error: 'Invalid characters in formula' };
    }

    // Evaluate safely
    const result = Function(`"use strict"; return (${processedFormula})`)();

    if (isNaN(result)) {
      return { result: null, error: 'Calculation resulted in NaN' };
    }

    return { result: Math.round(result * 100) / 100 }; // Round to 2 decimal places
  } catch (error) {
    return { result: null, error: `Calculation error: ${(error as Error).message}` };
  }
}

shared/types.ts.content:
// shared/types.ts
// ------------------------------------------------------------
// 1Ô∏è‚É£ General‚Äëpurpose types & enums
// ------------------------------------------------------------

/**
 * Plain string alias for UUID values.
 *
 * Using a dedicated alias makes the intent of the field explicit
 * while keeping the runtime type unchanged (still `string`).
 */
export type UUID = string;

/**
 * ISO‚Äë8601 formatted date‚Äëtime string.
 *
 * All timestamps coming from the database (`timestamp with time zone`)
 * are represented with this type.
 */
export type ISODateString = string;

/**
 * Severity levels for `ErrorItem.severity`.
 *
 * The literal tuple is exported as a **const** so that TypeScript can
 * infer a union type (`"low" | "medium" | "high" | "critical"`).
 */
export const ErrorSeverityEnum = [
  "low",
  "medium",
  "high",
  "critical",
] as const;
export type ErrorSeverity = typeof ErrorSeverityEnum[number];

/**
 * Question types for `QuestionConfig.type`.
 */
export const QuestionTypeEnum = [
  "text",
  "number",
  "date",
  "select",
  "radio",
  "checkbox",
  "measurement",
  "calculated",
] as const;
export type QuestionType = typeof QuestionTypeEnum[number];

/**
 * Cell value types for Excel parsing - includes all possible cell types
 * used in the excel-parser service.
 */
export const CellValueTypeEnum = [
  "text",
  "number", 
  "date",
  "select",
  "checkbox",
  "yes_no",      // √∫j
  "true_false",  // √∫j
  "measurement", // √∫j
  "calculated",  // √∫j
] as const;
export type CellValueType = typeof CellValueTypeEnum[number] | null;

/**
 * Template categories for `Template.type`.
 */
export const TemplateTypeEnum = [
  "protocol",
  "questions",
  "unified",
] as const;
export type TemplateType = typeof TemplateTypeEnum[number];
export type GroundingAnswer = 'ok' | 'not_ok' | 'not_applicable';


// ------------------------------------------------------------
// 2Ô∏è‚É£ Errors & measurement data
// ------------------------------------------------------------

/**
 * Single error record ‚Äì mirrors the `errors` table.
 */
export interface ErrorItem {
  /** Primary key */
  id: UUID;

  /** Short description */
  description: string;

  /** Category ‚Äì e.g. "electrical", "mechanical" */
  category: string;

  /** Severity ‚Äì restricted to the `ErrorSeverity` enum */
  severity: ErrorSeverity;

  /** Optional location information */
  location?: string;

  /** Optional image URL */
  imageUrl?: string;

  /** Free‚Äëform notes */
  notes?: string;

  /** Creation timestamp */
  createdAt: ISODateString;
}

/**
 * Niedervolt‚Äëmeasurement record ‚Äì mirrors the `niedervolt_measurements` table.
 */
export interface NiedervoltMeasurement {
  id: UUID;
  measurementType: string;
  description: string;
  value1: string;
  value2: string;
  value3: string;
  unit: string;
  notes: string;
}

// ------------------------------------------------------------
// 3Ô∏è‚É£ Form data (the JSON payload stored in `protocols.form_data`)
// ------------------------------------------------------------

/** Grounding check answers - OK/NOT OK/N.A. for each question */

export interface FormData {
  /** Arbitrary question-answer map */
  answers: Record<string, unknown>;

  /** Optional reception date (ISO-8601) */
  receptionDate?: ISODateString;

  /** Language code ‚Äì e.g. "hu", "de" */
  language?: string;

  /** Base64 or URL representation of the signature */
  signature?: string;

  /** Human-readable name of the signer */
  signatureName?: string;

  /** Flag indicating whether the protocol is completed */
  completed?: boolean;

  /** List of errors attached to the form */
  errors?: ErrorItem[];

  /** Optional list of low-voltage measurements */
  niedervoltMeasurements?: NiedervoltMeasurement[];

  /** Grounding check answers - OK / NOT OK / Not Applicable */
  groundingCheckAnswers?: Record<string, GroundingAnswer>;

  customGroundingTexts?: { [key: string]: string };
}

// ------------------------------------------------------------
// 4Ô∏è‚É£ Question configuration (table `question_configs`)
// ------------------------------------------------------------

/**
 * Localized text structure for multi-language support
 */
export interface LocalizedText {
  hu: string;
  de: string;
}

/**
 * Question group structure with logical key and localized title
 */
export interface QuestionGroup {
  /** Logical identifier for the group (e.g. "electrical_check") */
  key: string;
  /** Localized group titles */
  title: LocalizedText;
}

export interface QuestionConfig {
  id: UUID;
  /** FK ‚Üí `templates.id` */
  templateId: UUID;
  /** Unique identifier of the question inside the template */
  questionId: string;

  // NEW STRUCTURE: Localized title object
  /** Localized question titles */
  title: LocalizedText;

  // DEPRECATED: Old structure (kept for backward compatibility)
  /** @deprecated Use title.hu instead */
  titleHu?: string;
  /** @deprecated Use title.de instead */
  titleDe?: string;

  /** Question type ‚Äì limited to `QuestionType` */
  type: QuestionType;
  /** Whether the field is mandatory */
  required: boolean;
  /** Placeholder text shown in the UI */
  placeholder?: string;
  /** Optional Excel‚Äëstyle cell reference (e.g. "A1") */
  cellReference?: string;
  /** Optional sheet name */
  sheetName?: string;
  /** If the question spans multiple cells */
  multiCell?: boolean;

  // NEW STRUCTURE: Group object with key and localized title
  /** Question group information */
  group?: QuestionGroup;

  // DEPRECATED: Old structure (kept for backward compatibility)
  /** @deprecated Use group.key instead */
  groupName?: string;
  /** @deprecated Use group.title.de instead */
  groupNameDe?: string;
  /** @deprecated Use group.key instead */
  groupKey?: string;

  /** Order of the group */
  groupOrder?: number;

  /** Conditional display key (replaces conditional_group_key) */
  conditionalKey?: string;
  /** @deprecated Use conditionalKey instead */
  conditionalGroupKey?: string;

  /** Unit label (e.g. "mm", "kg") */
  unit?: string;
  /** Minimum numeric value (if applicable) */
  minValue?: number;
  /** Maximum numeric value (if applicable) */
  maxValue?: number;
  /** Formula used for calculated fields */
  calculationFormula?: string;
  /** List of question IDs that feed the formula (stored as JSONB) */
  calculationInputs?: string[];
  /** Radio button options */
  options?: string[];
}

// ------------------------------------------------------------
// 5Ô∏è‚É£ Template definition (table `templates`)
// ------------------------------------------------------------

export interface Template {
  id: UUID;
  /** Human‚Äëreadable name */
  name: string;
  /** Category ‚Äì limited to `TemplateType` */
  type: TemplateType;
  /** Language of the stored file ‚Äì "multilingual", "hu", "de", ‚Ä¶ */
  language: string;
  /** Original filename */
  fileName: string;
  /** Full storage path (e.g. "templates/xyz.xlsx") */
  filePath: string;
  /** Upload timestamp */
  uploadedAt: ISODateString;
  /** Whether the template is currently active */
  isActive: boolean;
}

// ------------------------------------------------------------
// 6Ô∏è‚É£ Protocol record (table `protocols`)
// ------------------------------------------------------------

export interface Protocol {
  id: UUID;

  /** JSONB column that contains the entire `FormData` payload */
  formData: FormData;

  /** Language code ‚Äì mirrors the column `language` */
  language: string;

  /** Record creation timestamp */
  createdAt: ISODateString;

  /** Timestamp of the last update (optional, maps to `updated_at`) */
  updatedAt?: ISODateString;
}

// ------------------------------------------------------------
// 7Ô∏è‚É£ XML Excel parsing types
// ------------------------------------------------------------

/**
 * Payload for XML Excel processing
 */
export interface XmlExcelPayload {
  templateId: UUID;
  fileName: string;
  filePath: string;
  language: string;
}

/**
 * Parsed row data from Excel processing
 */
export interface ParsedRow {
  questionId: string;
  value: unknown;
  cellReference?: string;
  sheetName?: string;
}

