import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { ScrollArea } from '@/components/ui/scroll-area';
import { useLanguageContext } from '@/components/language-provider';
import { formatDate } from '@/lib/utils';
import { Upload, Settings, FileSpreadsheet, CheckCircle, XCircle, Eye, Edit, Home, Trash2, X, Download, Loader2, FileText, User } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { ProfileSettings } from '@/components/profile-settings';

interface Template {
  id: string;
  name: string;
  type: string;
  language: string;
  fileName: string;
  is_active: boolean;
  uploaded_at: string;
}

interface LocalTemplate {
  id: string;
  name: string;
  name_de: string;
  type: string;
  language: string;
  path: string;
  description?: string;
  description_de?: string;
}

interface HybridTemplateData {
  local: LocalTemplate[];
  remote: Template[];
  current: {
    templateId: string;
    loadStrategy: string;
  };
}

interface AdminProps {
  onBack: () => void;
  onHome?: () => void;
}

export function Admin({ onBack, onHome }: AdminProps) {
  const { t, language } = useLanguageContext();
  const { toast } = useToast();
  const [templates, setTemplates] = useState<Template[]>([]);
  const [loading, setLoading] = useState(false);
  const [activatingId, setActivatingId] = useState<string | null>(null);
  const [previewData, setPreviewData] = useState<any>(null);
  const [previewOpen, setPreviewOpen] = useState(false);

  // K√ºl√∂n state a k√©t t√≠pus√∫ felt√∂lt√©shez
  const [questionsUpload, setQuestionsUpload] = useState({
    name: '',
    file: null as File | null,
  });

  const [protocolUpload, setProtocolUpload] = useState({
    name: '',
    file: null as File | null,
  });

  const [hybridTemplates, setHybridTemplates] = useState<HybridTemplateData | null>(null);
  const [selectedTemplate, setSelectedTemplate] = useState<string>('');
  const [loadStrategy, setLoadStrategy] = useState<string>('local_first');

  useEffect(() => {
    fetchTemplates();
    fetchHybridTemplates();
  }, []);

  const fetchTemplates = async () => {
    try {
      const response = await fetch('/api/admin/templates');
      if (response.ok) {
        const data = await response.json();
        setTemplates(data);
      }
    } catch (error) {
      console.error('Error fetching templates:', error);
      toast({
        title: t.error,
        description: 'Failed to fetch templates',
        variant: 'destructive',
      });
    }
  };

  const fetchHybridTemplates = async () => {
    try {
      const response = await fetch('/api/admin/templates/available');
      if (response.ok) {
        const data = await response.json();
        setHybridTemplates(data);
        setSelectedTemplate(data.current.templateId);
        setLoadStrategy(data.current.loadStrategy);
      }
    } catch (error) {
      console.error('Error fetching hybrid templates:', error);
    }
  };

  const handleTemplateSelect = async () => {
    if (!selectedTemplate) return;

    setLoading(true);
    try {
      const response = await fetch('/api/admin/templates/select', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          templateId: selectedTemplate,
          loadStrategy: loadStrategy
        }),
      });

      if (response.ok) {
        const result = await response.json();
        toast({
          title: t.success,
          description: `Template v√°lt√°s sikeres: ${result.template?.name || 'Sablon'}`,
        });
        fetchHybridTemplates();
      } else {
        throw new Error('Template selection failed');
      }
    } catch (error) {
      console.error('Error selecting template:', error);
      toast({
        title: t.error,
        description: 'Template v√°lt√°s sikertelen',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  // K√©rd√©s sablon f√°jl kiv√°laszt√°sa
  const handleQuestionsFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setQuestionsUpload({ ...questionsUpload, file });
    }
  };

  // Protokoll sablon f√°jl kiv√°laszt√°sa
  const handleProtocolFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setProtocolUpload({ ...protocolUpload, file });
    }
  };

  // K√©rd√©s sablon felt√∂lt√©se
  const handleQuestionsUpload = async () => {
    if (!questionsUpload.file || !questionsUpload.name) {
      toast({
        title: t.error,
        description: language === 'de' ? 'Bitte Namen und Datei angeben' : 'K√©rlek add meg a nevet √©s v√°lassz f√°jlt',
        variant: 'destructive',
      });
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('file', questionsUpload.file);
    formData.append('name', questionsUpload.name);
    formData.append('type', 'unified'); // Mindig unified
    formData.append('language', 'multilingual'); // Mindig multilingual

    try {
      const response = await fetch('/api/admin/templates/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        toast({
          title: t.success,
          description: language === 'de' ? 'Fragenvorlage erfolgreich hochgeladen' : 'K√©rd√©s sablon sikeresen felt√∂ltve',
        });
        setQuestionsUpload({ name: '', file: null });
        fetchTemplates();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Upload failed');
      }
    } catch (error: any) {
      console.error('Error uploading questions template:', error);
      toast({
        title: t.error,
        description: error.message || 'Failed to upload template',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  // Protokoll sablon felt√∂lt√©se
  const handleProtocolUpload = async () => {
    if (!protocolUpload.file || !protocolUpload.name) {
      toast({
        title: t.error,
        description: language === 'de' ? 'Bitte Namen und Datei angeben' : 'K√©rlek add meg a nevet √©s v√°lassz f√°jlt',
        variant: 'destructive',
      });
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('file', protocolUpload.file);
    formData.append('name', protocolUpload.name);
    formData.append('type', 'protocol'); // Mindig protocol
    formData.append('language', 'multilingual'); // Mindig multilingual

    try {
      const response = await fetch('/api/admin/templates/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        toast({
          title: t.success,
          description: language === 'de' ? 'Protokollvorlage erfolgreich hochgeladen' : 'Protokoll sablon sikeresen felt√∂ltve',
        });
        setProtocolUpload({ name: '', file: null });
        fetchTemplates();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Upload failed');
      }
    } catch (error: any) {
      console.error('Error uploading protocol template:', error);
      toast({
        title: t.error,
        description: error.message || 'Failed to upload template',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const handleActivate = async (templateId: string) => {
    setActivatingId(templateId);
    try {
      const response = await fetch(`/api/admin/templates/${templateId}/activate`, {
        method: 'POST',
      });

      if (response.ok) {
        toast({
          title: t.success,
          description: 'Template activated successfully',
        });
        fetchTemplates();
      } else {
        throw new Error('Activation failed');
      }
    } catch (error) {
      console.error('Error activating template:', error);
      toast({
        title: t.error,
        description: 'Failed to activate template',
        variant: 'destructive',
      });
    } finally {
      setActivatingId(null);
    }
  };

  const handlePreview = async (templateId: string) => {
    try {
      const [templateResponse, questionsResponse] = await Promise.all([
        fetch(`/api/admin/templates/${templateId}/preview`),
        fetch('/api/questions/hu')
      ]);

      if (templateResponse.ok) {
        const templateData = await templateResponse.json();
        let questionsData = [];

        if (questionsResponse.ok) {
          questionsData = await questionsResponse.json();
        }

        console.log('Template preview:', templateData);
        setPreviewData({ ...templateData, questions: questionsData });
        setPreviewOpen(true);
      } else {
        toast({
          title: t.error,
          description: 'Failed to load template preview',
          variant: 'destructive',
        });
      }
    } catch (error) {
      console.error('Error previewing template:', error);
      toast({
        title: t.error,
        description: 'Error loading template preview',
        variant: 'destructive',
      });
    }
  };

  const handleDownload = (templateId: string) => {
    window.location.href = `/api/admin/templates/${templateId}/download`;
  };

  const handleDelete = async (templateId: string, templateName: string) => {
    if (!confirm(`Biztosan t√∂r√∂lni szeretn√©d a(z) "${templateName}" sablont? Ez a m≈±velet nem vonhat√≥ vissza.`)) {
      return;
    }

    try {
      const response = await fetch(`/api/admin/templates/${templateId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        toast({
          title: t.success,
          description: 'Sablon sikeresen t√∂r√∂lve',
        });
        fetchTemplates();
      } else {
        throw new Error('Delete failed');
      }
    } catch (error) {
      console.error('Error deleting template:', error);
      toast({
        title: t.error,
        description: 'Sablon t√∂rl√©se sikertelen',
        variant: 'destructive',
      });
    }
  };

  const filteredTemplates = templates;

  return (
    <div className="min-h-screen bg-light-surface">
