// src/components/stable-input.tsx
import React from "react";
// √öJ IMPORT-ok a shadcn/ui komponensekb≈ël
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface StableInputProps {
  questionId: string;
  // B≈ëv√≠tj√ºk a lehets√©ges t√≠pusokat
  type?: 'text' | 'number' | 'email' | 'phone' | 'textarea' | 'select';
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  className?: string;
  min?: number;
  max?: number;
  maxLength?: number; // √öj prop a karakter limithez
  step?: string | number;
  onKeyDown?: (e: React.KeyboardEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  options?: string; // √öj prop a select opci√≥khoz (vessz≈ëvel elv√°lasztva)
  required?: boolean;
}

export function StableInput({
  questionId,
  type = 'text',
  value,
  onChange,
  placeholder,
  className,
  min,
  max,
  maxLength,
  step,
  onKeyDown,
  options,
  required,
}: StableInputProps) {

  // Speci√°lis esem√©nykezel≈ë a telefonsz√°mhoz
  const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const validPhoneRegex = /^[0-9+\-() ]*$/;
    if (validPhoneRegex.test(e.target.value)) {
      onChange(e.target.value);
    }
  };

  const baseClassName = "w-full focus:outline-none focus:ring-2 focus:ring-otis-blue focus:border-transparent";

  // A `type` alapj√°n a megfelel≈ë komponenst adjuk vissza
  switch (type) {
    case 'textarea':
      return (
        <Textarea
          value={value || ''}
          onChange={(e) => onChange(e.target.value)}
          onKeyDown={onKeyDown}
          placeholder={placeholder}
          required={required}
          maxLength={maxLength}
          className={`${baseClassName} ${className || ''}`}
        />
      );

    case 'select':
      const selectOptions = options ? options.split(',') : [];
      return (
        <Select value={value || ''} onValueChange={onChange} required={required}>
          <SelectTrigger className={`${baseClassName} ${className || ''}`}>
            <SelectValue placeholder={placeholder || "V√°lasszon..."} />
          </SelectTrigger>
          <SelectContent>
            {selectOptions.map((option) => (
              <SelectItem key={option.trim()} value={option.trim()}>
                {option.trim()}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      );

    case 'phone':
      return (
        <Input
          type="tel" // HTML `tel` t√≠pus a mobil billenty≈±zet miatt
          value={value || ''}
          onChange={handlePhoneChange} // Itt haszn√°ljuk a sz≈±r≈ët
          onKeyDown={onKeyDown}
          placeholder={placeholder}
          required={required}
          className={`${baseClassName} ${className || ''}`}
        />
      );

    // Az √∂sszes t√∂bbi `<input>`-alap√∫ t√≠pus
    case 'text':
    case 'number':
    case 'email':
    default:
      return (
        <Input
          type={type}
          value={value || ''}
          onChange={(e) => onChange(e.target.value)}
          onKeyDown={onKeyDown}
          placeholder={placeholder}
          min={min}
          max={max}
          maxLength={maxLength}
          step={step}
          required={required}
          className={`${baseClassName} ${className || ''}`}
        />
      );
  }
}

