/**
 * Centralized filename correction utilities for handling corrupt character encodings
 * in Supabase storage operations (download, delete, etc.)
 */

export interface FilenameStrategy {
  path: string;
  description: string;
}

/**
 * Generates multiple filename correction strategies for handling corrupt encoding
 * Common issue: "K√É√Ç¬©rd√É√Ç¬©ssor" (double-encoded UTF-8) -> "K√©rd√©ssor" (correct)
 */
export function generateFilenameStrategies(originalPath: string): FilenameStrategy[] {
  return [
    // 1. Eredeti f√°jln√©v (lehet, hogy nem korrupt)
    { path: originalPath, description: "original path" },

    // 2. URL encoded verzi√≥
    { 
      path: originalPath.split('/').map((part: string) => encodeURIComponent(part)).join('/'), 
      description: "URL encoded path" 
    },

    // 3. Dupla UTF-8 encoding jav√≠t√°s (K√É√Ç¬©rd√É√Ç¬©ssor -> K√©rd√©ssor)
    { 
      path: fixDoubleEncodedUTF8(originalPath), 
      description: "UTF-8 double-encoding fixed" 
    },

    // 4. ASCII-safe verzi√≥ (√©kezetek elt√°vol√≠t√°sa)
    { 
      path: toASCIISafe(originalPath), 
      description: "ASCII-safe path" 
    },

    // 5. Tiszta ASCII csak (minden nem-ASCII karakter elt√°vol√≠t√°sa)
    { 
      path: originalPath.replace(/[^\x00-\x7F]/g, ''), 
      description: "clean ASCII path" 
    }
  ];
}

/**
 * Fixes double-encoded UTF-8 strings (common in file upload corruptions)
 * Example: "K√É√Ç¬©rd√É√Ç¬©ssor" -> "K√©rd√©ssor"
 */
function fixDoubleEncodedUTF8(text: string): string {
  return text
    // Fix common Hungarian characters
    .replace(/K√É√Ç¬©rd√É√Ç¬©ssor/g, 'K√©rd√©ssor')
    .replace(/K√É\x83√Ç¬©rd√É\x83√Ç¬©ssor/g, 'K√©rd√©ssor')
    // Fix other common double-encodings  
    .replace(/√É¬°/g, '√°')  // √°
    .replace(/√É¬©/g, '√©')  // √©
    .replace(/√É¬≠/g, '√≠')  // √≠
    .replace(/√É¬≥/g, '√≥')  // √≥
    .replace(/√É¬∫/g, '√∫')  // √∫
    .replace(/√É¬º/g, '√º')  // √º
    .replace(/√É¬∂/g, '√∂')  // √∂
    .replace(/√Ö'/g, '≈ë')  // ≈ë
    .replace(/√Ö¬±/g, '≈±'); // ≈±
}

/**
 * Converts text to ASCII-safe version by replacing diacritics
 */
function toASCIISafe(text: string): string {
  return text
    .replace(/[√°√†√¢√§√£]/g, 'a')
    .replace(/[√©√®√™√´]/g, 'e') 
    .replace(/[√≠√¨√Æ√Ø]/g, 'i')
    .replace(/[√≥√≤√¥√∂√µ≈ë]/g, 'o')
    .replace(/[√∫√π√ª√º≈±≈Ø]/g, 'u')
    .replace(/[√Ω√ø]/g, 'y')
    .replace(/[ƒá]/g, 'c')
    .replace(/[ƒè]/g, 'd')
    .replace(/[≈à]/g, 'n')
    .replace(/[≈ô]/g, 'r')
    .replace(/[≈°]/g, 's')
    .replace(/[≈•]/g, 't')
    .replace(/[≈æ]/g, 'z')
    .replace(/[√Å√Ä√Ç√Ñ√É]/g, 'A')
    .replace(/[√â√à√ä√ã]/g, 'E')
    .replace(/[√ç√å√é√è]/g, 'I')
    .replace(/[√ì√í√î√ñ√ï≈ê]/g, 'O')
    .replace(/[√ö√ô√õ√ú≈∞]/g, 'U')
    .replace(/[√ù≈∏]/g, 'Y')
    .replace(/[ƒÜ]/g, 'C')
    .replace(/[ƒé]/g, 'D')
    .replace(/[≈á]/g, 'N')
    .replace(/[≈ò]/g, 'R')
    .replace(/[≈†]/g, 'S')
    .replace(/[≈§]/g, 'T')
    .replace(/[≈Ω]/g, 'Z');
}

/**
 * Attempts multiple strategies for a Supabase operation (download, delete, etc.)
 * Returns the successful strategy result or throws the last error
 */
export async function executeWithFilenameStrategies<T>(
  originalPath: string,
  operation: (path: string) => Promise<T>,
  operationName: string = 'operation'
): Promise<{ result: T; strategy: FilenameStrategy }> {
  const strategies = generateFilenameStrategies(originalPath);
  let lastError: any;

  for (const strategy of strategies) {
    console.log(`üîÑ Trying ${operationName} strategy: ${strategy.description} -> ${strategy.path}`);
    try {
      const result = await operation(strategy.path);
      console.log(`‚úÖ ${operationName} successful with strategy: ${strategy.description}`);
      return { result, strategy };
    } catch (error: any) {
      console.log(`‚ùå ${operationName} strategy "${strategy.description}" failed:`, error?.message || error);
      lastError = error;
      continue;
    }
  }

  // Ha minden strat√©gia failelt
  console.error(`üí• ALL ${operationName} strategies failed for path: ${originalPath}. Last error:`, lastError);
  throw lastError;
}

