import express from 'express';
import multer from 'multer';
import path from 'path';
import * as fs from 'fs';
import { storage } from '../storage.js';
import { supabaseStorage } from '../services/supabase-storage.js';
import { excelParserService } from '../services/excel-parser.js';
import { hybridTemplateLoader } from '../services/hybrid-template-loader.js';
import { clearQuestionsCache } from '../routes.js';

const router = express.Router();

const uploadDir = process.env.NODE_ENV === 'production' ? '/tmp' : path.join(process.cwd(), 'uploads');
if (process.env.NODE_ENV !== 'production' && !fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}
const upload = multer({ dest: uploadDir });

// GET /api/admin/templates/available - Helyi √©s t√°voli sablonok list√°z√°sa
router.get("/templates/available", async (_req, res) => {
  try {
    const allTemplates = await hybridTemplateLoader.getAllAvailableTemplates();
    const activeTemplate = await storage.getActiveTemplate('unified', 'multilingual');
    res.json({
      ...allTemplates,
      current: {
        templateId: activeTemplate?.id,
      }
    });
  } catch (error) {
    console.error("Error fetching available templates:", error);
    res.status(500).json({ message: "Failed to fetch available templates" });
  }
});

// POST /api/admin/templates/select - Sablon kiv√°laszt√°sa
router.post("/templates/select", async (req, res) => {
  try {
    const { templateId, loadStrategy } = req.body;
    if (!templateId) {
      return res.status(400).json({ message: "Template ID is required" });
    }
    console.log(`üìÑ Selecting template: ${templateId} with strategy: ${loadStrategy || 'local_first'}`);

    const templateResult = await hybridTemplateLoader.loadTemplate(
      templateId,
      "unified",
      "multilingual"
    );

    console.log(`‚úÖ Template selection processed for: ${templateResult.templateInfo.name || templateResult.templateInfo.file_name}`);
    res.json({ success: true });
  } catch (error) {
    console.error("‚ùå Error selecting template:", error);
    res.status(500).json({ message: "Failed to select template" });
  }
});

// GET /api/admin/templates - Felt√∂lt√∂tt (adatb√°zisban l√©v≈ë) sablonok list√°z√°sa
router.get("/templates", async (_req, res) => {
  try {
    const templates = await storage.getAllTemplates();
    res.json(templates);
  } catch (error) {
    console.error("Error fetching templates:", error);
    res.status(500).json({ message: "Failed to fetch templates" });
  }
});

// =========================================================
// === VISSZA√ÅLL√çTOTT FUNKCI√ì: TEMPLATE F√ÅJL LET√ñLT√âSE ===
// =========================================================
router.get("/templates/:id/download", async (req, res) => {
  try {
    const templateId = req.params.id;
    console.log(`[DOWNLOAD] Request received for template ID: ${templateId}`);

    // 1. Lek√©rj√ºk a sablon adatait az adatb√°zisb√≥l az ID alapj√°n
    const template = await storage.getTemplate(templateId);

    if (!template) {
      console.error(`[DOWNLOAD] Error: Template not found with ID: ${templateId}`);
      return res.status(404).json({ message: "Template not found" });
    }

    // 2. Ellen≈ërizz√ºk, hogy van-e f√°jl √∫tvonal megadva
    const filePath = template.file_path;
    if (!filePath) {
      console.error('[DOWNLOAD] Error: file_path is missing for this template.');
      return res.status(500).json({ message: 'File path is missing for this template.' });
    }

    // 3. Let√∂ltj√ºk a f√°jlt a Supabase Storage-b√≥l
    // Mivel a `downloadFile` helyi f√°jlba ment, l√©trehozunk egy temp √∫tvonalat
    const tempLocalPath = path.join(uploadDir, `download-${Date.now()}-${template.file_name}`);

    console.log(`[DOWNLOAD] Attempting to download from storage path: ${filePath} to ${tempLocalPath}`);
    await supabaseStorage.downloadFile(filePath, tempLocalPath);
    console.log(`[DOWNLOAD] File downloaded successfully to temp path.`);

    // 4. Az Express `res.download` funkci√≥j√°val elk√ºldj√ºk a f√°jlt, ami a k√ºld√©s ut√°n t√∂rli is azt
    res.download(tempLocalPath, template.file_name, (err) => {
      if (err) {
        console.error("Error sending file to client:", err);
      }

      // A callback biztos√≠tja, hogy a f√°jl t√∂rl√©se a k√ºld√©s ut√°n t√∂rt√©njen
      fs.unlink(tempLocalPath, (unlinkErr) => {
        if (unlinkErr) console.error("Error deleting temp file:", unlinkErr);
        else console.log(`‚úÖ Cleaned up temporary file: ${tempLocalPath}`);
      });
    });

  } catch (error) {
    console.error("‚ùå Error during template download:", error);
    res.status(500).json({ message: "Failed to download template file." });
  }
});

// POST /api/admin/templates/upload - √öj sablon felt√∂lt√©se
router.post("/templates/upload", upload.single('file'), async (req: any, res) => {
  try {
    if (!req.file) {
      return res.status(400).json({ message: "No file uploaded." });
    }

    const { originalname, path: tempPath } = req.file;
    const { name, type, language } = req.body;

    if (!type) {
      return res.status(400).json({ message: "Template 'type' ('unified' or 'protocol') is required." });
    }

    const storagePath = `templates/${Date.now()}-${originalname}`;
    await supabaseStorage.uploadFile(tempPath, storagePath);
    console.log(`[Upload] File uploaded to Supabase at: ${storagePath}`);

    const templateType = type;

    const newTemplate = await storage.createTemplate({
      name: name || originalname,
      type: templateType,
      language: language || 'multilingual',
      file_name: originalname,
      file_path: storagePath,
    });
    console.log(`[Upload] DB entry created for template ID: ${newTemplate.id}`);

    if (templateType === 'unified' || templateType === 'questions') {
      console.log(`Parsing questions for template of type "${templateType}"...`);
      const questions = await excelParserService.parseQuestionsFromExcel(tempPath);
      console.log(`‚úÖ Parsed ${questions.length} questions from template.`);

      for (const q of questions) {
        if (!q.questionId) {
          console.warn("Skipping question due to missing questionId:", q);
          continue;
        }

        await storage.createQuestionConfig({
          ...q,
          template_id: newTemplate.id,
          question_id: q.questionId,
          cell_reference: q.cellReference ?? null,
        });
      }
    } else {
      console.log(`Skipping question parsing for template of type "${templateType}".`);
    }

    res.status(201).json({ success: true, template: newTemplate });

  } catch (error) {
    console.error("‚ùå Error uploading template:", error);
    res.status(500).json({ message: "Failed to upload template." });
  } finally {
    if (req.file && req.file.path) {
      fs.unlink(req.file.path, (err) => {
        if (err) console.error("Error deleting temp file:", err);
      });
    }
  }
});

// POST /api/admin/templates/:id/activate - Sablon aktiv√°l√°sa
router.post("/templates/:id/activate", async (req, res) => {
  try {
    await storage.setActiveTemplate(req.params.id);

    hybridTemplateLoader.clearCache();
    console.log('‚úÖ Template cache cleared after activation.');
    clearQuestionsCache();
    res.json({ success: true });
  } catch (error) {
    console.error("Error activating template:", error);
    res.status(500).json({ message: "Failed to activate template" });
  }
});

// DELETE /api/admin/templates/:id - Sablon t√∂rl√©se
router.delete("/templates/:id", async (req, res) => {
  try {
    const templateId = req.params.id;
    const template = await storage.getTemplate(templateId);

    await storage.deleteQuestionConfigsByTemplate(templateId);

    if (template?.file_path) {
      await supabaseStorage.deleteFile(template.file_path);
    }

    await storage.deleteTemplate(templateId);

    res.json({ success: true });
  } catch (error) {
    console.error("Error deleting template:", error);
    res.status(500).json({ message: "Failed to delete template" });
  }
});

export const adminRoutes = router;

server/services/niedervolt-excel-service.content:
import JSZip from 'jszip';
import fs from 'fs';
import { storage } from '../storage.js';
import { templateLoader } from './template-loader.js';

interface MeasurementRow {
  id: string;
  measurementType: string;
  description: string;
  value1: string;
  value2: string;
  value3: string;
  unit: string;
  notes: string;
}

interface NiedervoltExcelServiceOptions {
  measurements: MeasurementRow[];
  language: 'hu' | 'de';
}

export class NiedervoltExcelService {
  /**
   * Integrates Niedervolt measurements into OTIS Excel template starting from row 667
   */
  async integrateMeasurements({ measurements, language }: NiedervoltExcelServiceOptions): Promise<Buffer> {
    try {
      console.log(`NIEDERVOLT: Loading template via Supabase Storage for language: ${language}`);

      // Load template buffer via the new template loader
      const templateBuffer = await templateLoader.loadTemplateBuffer('protocol', language);
      console.log(`NIEDERVOLT: Using loaded template (${templateBuffer.length} bytes)`);

      // Load the Excel template using JSZip
      const zip = new JSZip();
      const workbook = await zip.loadAsync(templateBuffer);

      // Read the worksheet XML
      const worksheetFile = workbook.file('xl/worksheets/sheet1.xml');
      if (!worksheetFile) {
        throw new Error('Could not find main worksheet in template');
      }

      let worksheetXml = await worksheetFile.async('text');

      // Parse existing cells to find the insertion point (row 667)
      const startingRow = 667;
      let currentRow = startingRow;

      // Generate XML for measurement rows
      const measurementCells: string[] = [];

      measurements.forEach((measurement, index) => {
        const rowNum = currentRow + index;

        // Column mapping for Niedervolt measurements:
        // A: Measurement Type, B: Description, C: Value1, D: Value2, E: Value3, F: Unit, G: Notes
        const cells = [
          { col: 'A', value: this.getMeasurementTypeName(measurement.measurementType, language) },
          { col: 'B', value: measurement.description },
          { col: 'C', value: measurement.value1 },
          { col: 'D', value: measurement.value2 }, 
          { col: 'E', value: measurement.value3 },
          { col: 'F', value: measurement.unit },
          { col: 'G', value: measurement.notes }
        ];

        cells.forEach(cell => {
          if (cell.value && cell.value.trim()) {
            const cellRef = `${cell.col}${rowNum}`;
            const cellXml = this.createCellXml(cellRef, cell.value);
            measurementCells.push(cellXml);
          }
        });
      });

      // Insert the measurement cells into the worksheet XML
      if (measurementCells.length > 0) {
        const cellsToInsert = measurementCells.join('\n      ');

        // Find the sheetData closing tag and insert before it
        const sheetDataEndIndex = worksheetXml.lastIndexOf('</sheetData>');
        if (sheetDataEndIndex !== -1) {
          worksheetXml = worksheetXml.slice(0, sheetDataEndIndex) + 
                        '      ' + cellsToInsert + '\n    ' +
                        worksheetXml.slice(sheetDataEndIndex);
        }
      }

      // Update the worksheet in the zip
      zip.file('xl/worksheets/sheet1.xml', worksheetXml);

      console.log(`NIEDERVOLT: Integrated ${measurements.length} measurement rows starting from row ${startingRow}`);

      // Generate the updated Excel file
      const updatedBuffer = await zip.generateAsync({
        type: 'nodebuffer',
        compression: 'DEFLATE',
        compressionOptions: { level: 6 }
      });

      return updatedBuffer;

    } catch (error) {
      console.error('NIEDERVOLT ERROR:', error);
      throw new Error(`Failed to integrate Niedervolt measurements: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Get localized measurement type name
   */
  private getMeasurementTypeName(typeId: string, language: 'hu' | 'de'): string {
    const types: Record<string, { hu: string; de: string }> = {
      'isolation': { hu: 'Isolationsmessung', de: 'Isolationsmessung' },
      'shortcircuit': { hu: 'Kurzschluss-strommessung', de: 'Kurzschluss-strommessung' },
      'voltage': { hu: 'Spannungsmessung', de: 'Spannungsmessung' },
      'continuity': { hu: 'Durchgangspr√ºfung', de: 'Durchgangspr√ºfung' },
      'insulation_resistance': { hu: 'Isolationswiderstand', de: 'Isolationswiderstand' },
      'earth_resistance': { hu: 'Erdungswiderstand', de: 'Erdungswiderstand' }
    };

    return types[typeId]?.[language] || typeId;
  }

  /**
   * Create Excel cell XML with proper formatting
   */
  private createCellXml(cellRef: string, value: string): string {
    // Determine if value is numeric
    const numericValue = parseFloat(value);
    const isNumeric = !isNaN(numericValue) && isFinite(numericValue);

    if (isNumeric) {
      // Numeric cell
      return `<c r="${cellRef}"><v>${numericValue}</v></c>`;
    } else {
      // Text cell - needs to be added to shared strings
      // For simplicity, we'll use inline string (less efficient but works)
      const escapedValue = value
        .replace(/&/g, '&amp;')
        .replace(/</g, '&lt;')
        .replace(/>/g, '&gt;')
        .replace(/"/g, '&quot;')
        .replace(/'/g, '&apos;');

      return `<c r="${cellRef}" t="inlineStr"><is><t>${escapedValue}</t></is></c>`;
    }
  }
}

export const niedervoltExcelService = new NiedervoltExcelService();

